import React, { useRef, useState } from "react";
import { FaHeart, FaThumbsUp, FaChevronLeft, FaChevronRight } from "react-icons/fa";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import { Autoplay } from "swiper/modules";

const postsData = [
  /* same postsData as before */
];

export default function RecentPosts() {
  const [posts, setPosts] = useState(postsData);
  const swiperRef = useRef(null);

  const handleLike = (id) => {
    setPosts((prev) =>
      prev.map((post) =>
        post.id === id ? { ...post, likes: post.likes + 1 } : post
      )
    );
  };

  const handleThumb = (id) => {
    setPosts((prev) =>
      prev.map((post) =>
        post.id === id ? { ...post, thumbs: post.thumbs + 1 } : post
      )
    );
  };

  const scrollLeft = () => {
    if (swiperRef.current) swiperRef.current.slidePrev();
  };

  const scrollRight = () => {
    if (swiperRef.current) swiperRef.current.slideNext();
  };

  return (
    <div className="w-full bg-gray-100 py-10 px-4 relative">
      <h2 className="text-3xl font-bold mb-8 text-center">Recent Posts</h2>

      {/* Swiper Slider */}
      <Swiper
        onSwiper={(swiper) => (swiperRef.current = swiper)}
        modules={[Autoplay]}
        spaceBetween={20}
        slidesPerView={1}
        autoplay={{ delay: 4000, disableOnInteraction: false }}
        breakpoints={{
          640: { slidesPerView: 1 },
          768: { slidesPerView: 2 },
          1024: { slidesPerView: 4 },
        }}
      >
        {posts.map((post) => (
          <SwiperSlide key={post.id}>
            <div className="flex flex-col rounded-2xl overflow-hidden shadow-lg hover:scale-[1.02] transition bg-white">
              {/* Image */}
              <div
                className="h-48 bg-cover bg-center relative"
                style={{ backgroundImage: `url(${post.img})` }}
              >
                <div className="absolute inset-0 bg-black/30"></div>
              </div>

              {/* Content Below Image */}
              <div className="p-4 flex flex-col justify-between h-full">
                <h3 className="text-lg font-bold mb-1">{post.title}</h3>
                <p className="text-sm text-gray-700 mb-2">{post.description}</p>

                {/* Profile + Date */}
                <div className="flex items-center justify-between mt-2">
                  <div className="flex items-center gap-2">
                    <img
                      src={post.profileImg}
                      alt={post.profile}
                      className="w-8 h-8 rounded-full border-2 border-gray-300"
                    />
                    <div>
                      <p className="text-sm font-semibold">{post.profile}</p>
                      <p className="text-xs text-gray-500">
                        {post.date} â€¢ {post.comments} comments
                      </p>
                    </div>
                  </div>

                  {/* Like / Thumb Buttons */}
                  <div className="flex gap-2">
                    <button
                      onClick={() => handleLike(post.id)}
                      className="flex items-center gap-1 bg-gray-100 px-2 py-1 rounded-lg hover:bg-gray-200 transition text-sm"
                    >
                      <FaHeart className="text-red-500 w-4 h-4" /> {post.likes}
                    </button>
                    <button
                      onClick={() => handleThumb(post.id)}
                      className="flex items-center gap-1 bg-gray-100 px-2 py-1 rounded-lg hover:bg-gray-200 transition text-sm"
                    >
                      <FaThumbsUp className="text-blue-500 w-4 h-4" /> {post.thumbs}
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>

      {/* Custom Left Button */}
      <button
        onClick={scrollLeft}
        className="absolute left-2 top-1/2 -translate-y-1/2 z-20
          bg-white/30 backdrop-blur-xl border border-white/20 shadow-lg
          p-2 rounded-full hover:scale-110 duration-300 flex"
      >
        <FaChevronLeft className="text-white" size={18} />
      </button>

      {/* Custom Right Button */}
      <button
        onClick={scrollRight}
        className="absolute right-2 top-1/2 -translate-y-1/2 z-20
          bg-white/30 backdrop-blur-xl border border-white/20 shadow-lg
          p-2 rounded-full hover:scale-110 duration-300 flex"
      >
        <FaChevronRight className="text-white" size={18} />
      </button>
    </div>
  );
}
