import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import { FaStar, FaMapMarkerAlt, FaClock, FaEye, FaBuilding } from "react-icons/fa";
import { FaShareAlt, FaPhone, FaComments, FaBed, FaBath, FaCar, FaShieldAlt } from "react-icons/fa";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

// Sample properties array
const properties = [
  {
    id: 1,
    title: "Luxury 2 BHK Apartment",
    price: "$120,000",
    location: "Delhi, India",
    description:
      "Spacious 2 BHK apartment with modern amenities, fully furnished, located in prime area with excellent connectivity.",
    beds: 2,
    baths: 2,
    parking: 1,
    security: "24/7",
    classification: "Premium",
    images: [
      "https://images.unsplash.com/photo-1560185127-6ed189bf02f4?q=80&w=1200",
      "https://images.unsplash.com/photo-1598928506311-c55ded91a20c?q=80&w=1200",
      "https://images.unsplash.com/photo-1580587771525-78b9dba3b914?q=80&w=1200",
    ],
    seller: { name: "Amit Sharma", avatar: "https://i.ibb.co/3mT9xzw/user1.png", date: "12 Jan 2025" },
  },
  {
    id: 2,
    title: "Family House",
    price: "$180,000",
    location: "Mumbai, India",
    description:
      "Cozy family house with large garden and parking. Ideal for families looking for comfort and security.",
    beds: 3,
    baths: 2,
    parking: 2,
    security: "24/7",
    classification: "Standard",
    images: [
      "https://images.unsplash.com/photo-1600607688969-a5bfcd646154",
      "https://images.unsplash.com/photo-1600566752355-35792bedcfea",
    ],
    seller: { name: "Shruti Patel", avatar: "https://i.ibb.co/7rnSKkj/user3.png", date: "03 Feb 2025" },
  },
];

export default function PropertiesDetails() {
  const { id } = useParams();
  const property = properties.find((p) => p.id === parseInt(id));
  const [openModal, setOpenModal] = useState(false);

  if (!property) return <div className="text-white p-5">Property not found!</div>;

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
  };

  return (
    <div className="min-h-screen bg-[#0f1629] text-white">

      {/* ðŸ”µ TOP BANNER */}
      <div
        className="w-full h-72 md:h-96 bg-cover bg-center flex items-center justify-center relative"
        style={{ backgroundImage: "url('https://images.unsplash.com/photo-1501183638710-841dd1904471?q=80&w=1600')" }}
      >
        <div className="absolute inset-0 bg-black/50"></div>
        <div className="relative text-center">
          <h1 className="text-4xl font-bold">{property.title}</h1>
        </div>
      </div>

      {/* ðŸ”µ MAIN SECTION */}
      <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-6 mt-20 px-4">

        {/* LEFT SIDE */}
        <div className="md:col-span-2 bg-[#121a2f] p-4 rounded-xl border border-gray-700 space-y-4">
          {/* Image Slider */}
          <Slider {...sliderSettings}>
            {property.images.map((img, index) => (
              <img key={index} src={img} className="rounded-xl w-full h-64 object-cover" />
            ))}
          </Slider>

          {/* Title, Price, Location */}
          <div className="mt-4">
            <h2 className="text-2xl font-bold">{property.title}</h2>
            <div className="flex items-center gap-4 text-gray-300 mt-2">
              <span className="flex items-center gap-1"><FaBuilding /> Real Estate</span>
              <span className="flex items-center gap-1"><FaMapMarkerAlt /> {property.location}</span>
              <span className="flex items-center gap-1"><FaClock /> 5 hours ago</span>
              <span className="flex items-center gap-1"><FaEye /> 876 Views</span>
              <span className="flex items-center gap-1 text-yellow-400"><FaStar /> 4.8 Rating</span>
            </div>
            <p className="text-blue-300 font-bold text-lg mt-2">Price: {property.price}</p>
          </div>

          {/* Description */}
          <div className="bg-[#0d1526] p-4 rounded-xl border border-gray-600">
            <h3 className="text-xl font-semibold mb-2">Description</h3>
            <p className="text-gray-200">{property.description}</p>
          </div>

          {/* Specifications */}
          <div className="bg-[#0d1526] p-4 rounded-xl border border-gray-600 grid grid-cols-2 sm:grid-cols-4 gap-4">
            <div className="flex flex-col items-center">
              <FaBed className="text-blue-400 mb-1" /> {property.beds} Beds
            </div>
            <div className="flex flex-col items-center">
              <FaBath className="text-blue-400 mb-1" /> {property.baths} Baths
            </div>
            <div className="flex flex-col items-center">
              <FaCar className="text-blue-400 mb-1" /> {property.parking} Car
            </div>
            <div className="flex flex-col items-center">
              <FaShieldAlt className="text-blue-400 mb-1" /> Security: {property.security}
            </div>
            <div className="flex flex-col items-center col-span-2 sm:col-span-4">
              Classification: {property.classification} | ID: {property.id}
            </div>
          </div>
        </div>

        {/* RIGHT SIDE */}
        <div className="bg-[#121a2f] p-4 rounded-xl border border-gray-700 space-y-4">

          {/* Seller Card */}
          <div className="bg-[#0d1526] p-4 rounded-lg text-center border border-gray-600">
            <img src={property.seller.avatar} className="w-20 h-20 rounded-full border-2 mx-auto" />
            <h3 className="mt-3 text-xl font-bold">{property.seller.name}</h3>
            <p className="text-gray-400">Real Estate Agent</p>
            <p className="text-gray-500 text-sm">Posted on: {property.seller.date}</p>
          </div>

          {/* Contact Buttons */}
          <div className="mt-6 border-t border-gray-600 pt-4 flex flex-col gap-3">
            <button className="bg-blue-700 py-2 rounded flex items-center justify-center gap-2" onClick={() => setOpenModal(true)}><FaComments /> Chat</button>
            <button className="bg-green-600 py-2 rounded flex items-center justify-center gap-2" onClick={() => setOpenModal(true)}><FaPhone /> Contact Me</button>
            <button className="bg-gray-700 py-2 rounded flex items-center justify-center gap-2" onClick={() => setOpenModal(true)}><FaShareAlt /> Share</button>
          </div>
        </div>
      </div>

      {/* POPUP MODAL */}
      {openModal && (
        <div className="fixed inset-0 bg-black/70 flex items-center justify-center">
          <div className="bg-white text-black p-5 rounded-lg w-80 shadow-xl">
            <h2 className="text-xl font-bold mb-2">Coming Soon</h2>
            <p>Feature will be available in the next update!</p>
            <button className="mt-4 bg-blue-600 px-4 py-2 text-white rounded" onClick={() => setOpenModal(false)}>Close</button>
          </div>
        </div>
      )}

    </div>
  );
}
