import { useParams } from "react-router-dom";

const postsData = [
  {
    id: 1,
    title: "Modern Apartment in Germany",
    img: "https://images.unsplash.com/photo-1507089947368-19c1da9775ae?auto=format&fit=crop&w=800&q=80",
    profile: "Emily Johnson",
    profileImg: "https://i.ibb.co/8zPxyzD/woman1.jpg",
    date: "Nov 25, 2025",
    comments: 12,
    description: "Spacious apartment with modern amenities in central Germany.",
  },
  // baaki posts...
];

export default function BlogDetails() {
  const { id } = useParams();
  const post = postsData.find((p) => p.id === parseInt(id));

  if (!post) return <p className="text-white">Post not found</p>;

  return (
    <div className="p-6 text-white">
      <img
        src={post.img}
        alt={post.title}
        className="w-full h-80 object-cover rounded-xl mb-4"
      />
      <h1 className="text-3xl font-bold mb-2">{post.title}</h1>
      <div className="flex items-center gap-3 mb-4">
        <img
          src={post.profileImg}
          alt={post.profile}
          className="w-12 h-12 rounded-full"
        />
        <div>
          <p className="font-bold">{post.profile}</p>
          <p className="text-gray-400 text-sm">{post.date} â€¢ {post.comments} comments</p>
        </div>
      </div>
      <p className="text-gray-300 leading-7">{post.description}</p>
    </div>
  );
}
