import React, { useState } from "react";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";

export default function SafetyTipsPage() {
  // Initial editable sections
  const initialSections = [
    {
      id: 1,
      title: "Watch out for email scams",
      description:
        "Scammers often impersonate companies asking for personal information. Never click suspicious links or provide passwords.",
      more: "Examples: fake invoice emails, password reset requests from unknown addresses.",
    },
    {
      id: 2,
      title: "Pay with a credit card",
      description:
        "Credit cards often provide buyer protection. Prefer card payments for large purchases and verify merchant legitimacy.",
      more: "Tip: Check for 'https' and the padlock icon before entering card details.",
    },
    {
      id: 3,
      title: "Check your statements",
      description:
        "Review bank and card statements regularly to spot unauthorized transactions early and report them immediately.",
      more: "Small recurring charges can indicate an unwanted subscription — cancel and dispute if needed.",
    },
    {
      id: 4,
      title: "Report the company",
      description:
        "If you suspect fraud, report the company to consumer protection agencies and to your bank or card provider.",
      more: "You can also leave reviews to warn others and submit complaints to relevant authorities.",
    },
  ];

  const [sections, setSections] = useState(initialSections);
  const [editId, setEditId] = useState(null);
  const [draft, setDraft] = useState({ title: "", description: "", more: "" });
  const [openMore, setOpenMore] = useState({});

  const startEdit = (sec) => {
    setEditId(sec.id);
    setDraft({ title: sec.title, description: sec.description, more: sec.more });
    setOpenMore((s) => ({ ...s, [sec.id]: false }));
  };

  const cancelEdit = () => {
    setEditId(null);
    setDraft({ title: "", description: "", more: "" });
  };

  const saveEdit = (id) => {
    setSections((prev) => prev.map((p) => (p.id === id ? { ...p, ...draft } : p)));
    setEditId(null);
    setDraft({ title: "", description: "", more: "" });
  };

  const toggleMore = (id) => {
    setOpenMore((s) => ({ ...s, [id]: !s[id] }));
  };

  return (
    <div className="min-h-screen bg-[#0a1124] text-white pt-24 px-4 md:px-0">
      <div className="flex flex-col md:flex-row gap-6 max-w-6xl mx-auto">
        {/* Desktop sidebar (vertical, unchanged) */}
        <div className="hidden md:block w-64">
          <Sidebar />
        </div>

        {/* Mobile horizontally scrollable sidebar */}
        <div className="flex md:hidden overflow-x-auto w-full mb-4">
          <div className="flex flex-row gap-3 px-2 flex-nowrap">
            {/* If your Sidebar component supports a prop to render compact/horizontal items, pass it (isMobile shown as example) */}
            <Sidebar isMobile />
            {/* If Sidebar cannot render horizontal items, replace the line above with manual links/buttons */}
          </div>
        </div>

        {/* Right content */}
        <div className="flex-1">
          <div className="bg-[#111827] p-4 md:p-6 rounded-xl shadow-lg">
            <h1 className="text-2xl md:text-3xl font-semibold mb-4">Safety Tips</h1>

            <div className="space-y-6">
              {sections.map((sec) => (
                <div key={sec.id} className="bg-[#0f1724] p-4 rounded-lg border border-gray-700">
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex-1">
                      {/* Title */}
                      {editId === sec.id ? (
                        <input
                          value={draft.title}
                          onChange={(e) => setDraft((d) => ({ ...d, title: e.target.value }))}
                          className="w-full bg-gray-900 border border-gray-600 rounded-md p-2 mb-2 text-white"
                        />
                      ) : (
                        <h2 className="text-lg font-semibold">{sec.title}</h2>
                      )}

                      {/* Description */}
                      {editId === sec.id ? (
                        <textarea
                          rows={3}
                          value={draft.description}
                          onChange={(e) => setDraft((d) => ({ ...d, description: e.target.value }))}
                          className="w-full bg-gray-900 border border-gray-600 rounded-md p-2 mb-2 text-white"
                        />
                      ) : (
                        <p className="text-gray-300 mb-2">{sec.description}</p>
                      )}

                      {/* more / view more */}
                      {editId === sec.id ? (
                        <>
                          <label className="block text-sm text-gray-400 mb-1">View more text</label>
                          <textarea
                            rows={2}
                            value={draft.more}
                            onChange={(e) => setDraft((d) => ({ ...d, more: e.target.value }))}
                            className="w-full bg-gray-900 border border-gray-600 rounded-md p-2 text-white"
                          />
                        </>
                      ) : (
                        <>
                          <div className="text-sm text-gray-400 mb-2">
                            {openMore[sec.id] ? sec.more : sec.more.length > 120 ? sec.more.slice(0, 120) + "..." : sec.more}
                          </div>

                          <div className="flex items-center gap-3">
                            <button onClick={() => toggleMore(sec.id)} className="text-sm text-blue-400 hover:underline">
                              {openMore[sec.id] ? "Show less" : "View more"}
                            </button>
                          </div>
                        </>
                      )}
                    </div>

                    {/* Edit controls */}
                    <div className="flex-shrink-0 flex flex-col items-end gap-2">
                      {editId === sec.id ? (
                        <div className="flex gap-2">
                          <button onClick={() => saveEdit(sec.id)} className="px-3 py-1 bg-green-600 hover:bg-green-700 rounded text-sm">
                            Save
                          </button>
                          <button onClick={cancelEdit} className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded text-sm">
                            Cancel
                          </button>
                        </div>
                      ) : (
                        <button onClick={() => startEdit(sec)} className="px-3 py-1 bg-blue-600 hover:bg-blue-700 rounded text-sm">
                          Edit
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Footer area inside right column */}
          <div className="mt-6">
            <Footer />
          </div>
        </div>
      </div>
    </div>
  );
}
