import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();

  const menuItems = [
    { label: "âœï¸ Edit Profile", path: "/edit-profile" },
    { label: "â­ My Favorites", path: "/my-favorites" },
    { label: "ðŸ“¢ My Ads", path: "/my-ads" },
    { label: "ðŸ›  Manage Ads", path: "/manage-ads" },
    { label: "ðŸ’³ Payments", path: "/payments" },
    { label: "ðŸ“¦ Orders", path: "/orders" },
    { label: "ðŸ”’ Safety Tips", path: "/safety-tips" },
    { label: "âš™ï¸ Settings", path: "/settings" },
  ];

  const handleLinkClick = () => {
    setTimeout(() => setIsOpen(false), 100);
  };

  return (
    <>
      {/* Mobile Toggle Button */}
      <div className="md:hidden flex justify-between items-center px-4 py-2 bg-[#111827] text-white">
        <h2 className="font-bold text-lg">Menu</h2>
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="bg-blue-700 px-4 py-2 rounded-lg text-white"
        >
          {isOpen ? "Close" : "Open"}
        </button>
      </div>

      {/* Overlay for Mobile */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/50 z-40 md:hidden"
          onClick={() => setIsOpen(false)}
        />
      )}

      {/* Sidebar */}
      <div
        className={`
          lg:relative lg:z-[1] md: top-0 left-0 h-full w-64 bg-[#111827] text-white p-6 shadow-xl border-r border-gray-700 z-50
          transform transition-transform duration-300 ease-in-out
          ${isOpen ? "translate-x-0" : "-translate-x-full"}
          md:translate-x-0 md:static md:block md:h-auto md:shadow-none md:border-none
          overflow-y-auto md:overflow-y-visible
        `}
        style={{ maxHeight: "100vh" }}
      >
        {/* Profile */}
        <div className="text-center mb-10">
          <img
            src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
            className="w-24 h-24 mx-auto rounded-full border-4 border-white/30"
          />
          <h2 className="text-xl font-semibold mt-3">John Doe</h2>
          <p className="text-white/70 text-sm">john.doe@example.com</p>
        </div>

        {/* Menu */}
        <div className="space-y-2">
          {menuItems.map((item, i) => (
            <Link
              key={i}
              to={item.path}
              onClick={handleLinkClick}
              className={`block px-4 py-2 rounded-lg font-medium transition
                ${location.pathname === item.path ? "bg-white/20" : "hover:bg-white/10"}`}
            >
              {item.label}
            </Link>
          ))}

          <button className="w-full px-4 py-2 mt-2 rounded-lg text-red-400 bg-red-600/20 hover:bg-red-600/30">
            ðŸšª Logout
          </button>
        </div>

        {/* Search Box */}
        <div className="mt-5 bg-[#0E1626]  rounded-xl">
         
          <input
            type="text"
            placeholder="Type your location..."
            className="w-full mt-2 p-3 rounded-lg bg-gray-800 border border-gray-600 focus:ring focus:ring-blue-500"
          />

          <label className="block font-semibold mt-4">All Categories</label>
          <select className="w-full p-3 border rounded-lg mt-2 bg-gray-800 text-white">
            <option>Select Category</option>
            <option>Apartments</option>
            <option>Villas</option>
            <option>Office</option>
          </select>

          <button className="w-full mt-4 py-3 rounded-lg font-semibold text-white bg-gradient-to-r from-blue-700 to-blue-400">
            Search
          </button>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
