import React from "react";
import {
  FaBuilding,
  FaStore,
  FaBriefcase,
  FaHome,
} from "react-icons/fa";
import CategorySlider from "../components/CategorySlider";

const Home = () => {
  const categories = [
    { name: "Commercial Land", icon: <FaBuilding size={22} />, count: 12 },
    { name: "Showrooms & Shops", icon: <FaStore size={22} />, count: 18 },
    { name: "Office Rooms", icon: <FaBriefcase size={22} />, count: 9 },
    { name: "Residential", icon: <FaHome size={22} />, count: 25 },
  ];

  return (
    <div className="w-full bg-[#07182E]">

      {/* Banner Section */}
      <div
        className="relative w-screen bg-cover bg-center"
        style={{
          backgroundImage:
            `url("https://images.unsplash.com/photo-1600585154340-be6161a56a0c?auto=format&fit=crop&w=1600&q=80")`,
          height: "70vh",
        }}
      >
        <div className="absolute inset-0 bg-black/50"></div>

        {/* Banner Content */}
        <div className="relative flex flex-col justify-center items-center h-full text-center px-4">
          <h1 className="text-white text-4xl font-bold">
            Your Property, Our Priority
          </h1>

          <p className="text-white text-lg mt-2">
            Buy • Sell • Rent • Luxury Estates • Apartments • Farmhouses
          </p>

          {/* Search Bar */}
          <div className="mt-5 bg-white/30 backdrop-blur-md border border-white/30 rounded-xl p-2 
            flex flex-col md:flex-row items-center gap-4 max-w-[800px] w-[90%]">

            <input
              type="text"
              placeholder="Find my property..."
              className="w-full md:w-[160px] bg-white p-2 rounded-md border"
            />

            <input
              type="text"
              placeholder="Search Location..."
              className="w-full md:w-[160px] bg-white p-2 rounded-md border"
            />

            <input type="date" className="bg-white p-2 w-full md:w-[130px] rounded-md border" />
            <input type="date" className="bg-white p-2 w-full md:w-[130px] rounded-md border" />

            <button className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded-lg">
              Search
            </button>
          </div>
        </div>

        {/* Floating Categories */}
        <div className="absolute bottom-[-50px] left-1/2 -translate-x-1/2 w-full px-6">
          <div className="max-w-[1000px] mx-auto grid grid-cols-2 md:grid-cols-4 gap-5">
            {categories.map((item, index) => (
              <div
                key={index}
                className="bg-white/25 backdrop-blur-lg border border-white/40 
                p-4 rounded-xl shadow-xl hover:scale-105 transition flex items-center gap-4"
              >
                <div className="text-blue-600 text-xl">{item.icon}</div>

                <div>
                  <h3 className="font-semibold">{item.name}</h3>
                  <p className="text-sm opacity-90">{item.count} Properties</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Space for Floating Boxes */}
      <div className="h-[70px]"></div>

      {/* Category Slider */}
      <CategorySlider />
    </div>
  );
};

export default Home;
