import { useState } from "react";
import { FaChevronLeft, FaChevronRight, FaStar } from "react-icons/fa";

const testimonialsData = [
  {
    id: 1,
    name: "Elizabeth",
    rating: 5,
    text:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
    avatar: "https://i.ibb.co/8zPxyzD/woman1.jpg",
  },
  {
    id: 2,
    name: "Michael",
    rating: 4,
    text:
      "Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip.",
    avatar: "https://i.ibb.co/TkhFn8c/man1.jpg",
  },
  {
    id: 3,
    name: "Sophia",
    rating: 5,
    text:
      "Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore.",
    avatar: "https://i.ibb.co/j6qx6Sk/woman2.jpg",
  },
];

export default function Testimonials() {
  const [index, setIndex] = useState(0);

  const next = () =>
    setIndex((prev) => (prev + 1) % testimonialsData.length);

  const prev = () =>
    setIndex((prev) =>
      prev === 0 ? testimonialsData.length - 1 : prev - 1
    );

  const active = testimonialsData[index];

  return (
    <div
      className="relative w-full py-20 bg-cover bg-center text-white"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1600585154340-be6161a56a0c?auto=format&fit=crop&w=1600&q=80')",
      }}
    >
      {/* Dark overlay */}
      <div className="absolute inset-0 bg-black/50"></div>

      <div className="relative z-10 max-w-3xl mx-auto text-center px-4">

        {/* Title */}
        <h1 className="text-4xl font-bold mb-10">Testimonials</h1>

        {/* Avatars Row */}
        <div className="flex justify-center gap-6 mb-8">
          {testimonialsData.map((p, i) => (
            <img
              key={p.id}
              src={p.avatar}
              className={`h-16 w-16 rounded-full border-4 transform transition-all duration-500 ${
                i === index
                  ? "scale-125 border-purple-500"
                  : "scale-90 opacity-60 border-white/50"
              }`}
              alt=""
            />
          ))}
        </div>

        {/* Arrows */}
        <button
          onClick={prev}
          className="absolute left-10 top-1/2 transform -translate-y-1/2 bg-white/20 p-3 rounded-full hover:bg-white/40"
        >
          <FaChevronLeft size={18} />
        </button>

        <button
          onClick={next}
          className="absolute right-10 top-1/2 transform -translate-y-1/2 bg-white/20 p-3 rounded-full hover:bg-white/40"
        >
          <FaChevronRight size={18} />
        </button>

        {/* Description */}
        <p className="text-lg text-gray-200 max-w-2xl mx-auto mb-6">
          “{active.text}”
        </p>

        {/* Name */}
        <h3 className="text-2xl font-semibold">{active.name}</h3>

        {/* Stars */}
        <div className="flex justify-center mt-2 mb-8 text-yellow-400">
          {Array.from({ length: active.rating }).map((_, i) => (
            <FaStar key={i} />
          ))}
        </div>

        {/* Button */}
        <button
          className="bg-purple-600 hover:bg-purple-700 text-white px-6 py-3 rounded-lg font-semibold transition"
        >
          View all Testimonials
        </button>
      </div>
    </div>
  );
}
