import React, { useEffect, useState } from "react";

const CounterSection = () => {
  const counters = [
    { label: "Total Agents", value: 50 },
    { label: "Total Sales", value: 120 },
    { label: "Total Projects", value: 35 },
    { label: "Happy Customers", value: 300 },
  ];

  const [counts, setCounts] = useState(counters.map(() => 0));

  useEffect(() => {
    counters.forEach((counter, index) => {
      let start = 0;
      const end = counter.value;
      const duration = 2000;
      const step = end / (duration / 20);

      const timer = setInterval(() => {
        start += step;
        if (start >= end) {
          start = end;
          clearInterval(timer);
        }

        setCounts((prev) => {
          const updated = [...prev];
          updated[index] = Math.floor(start);
          return updated;
        });
      }, 20);
    });
  }, []);

  return (
    <div
      className="w-full h-[60vh] bg-cover bg-center flex items-center justify-center my-10"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1501183638710-841dd1904471?auto=format&fit=crop&w=1400')",
      }}
    >
      <div className="w-full h-full bg-black/60 flex items-center justify-center">
        <div className="grid grid-cols-2 md:grid-cols-4 gap-8 text-center text-white px-4">
          {counters.map((item, i) => (
            <div
              key={i}
              className="backdrop-blur-md bg-white/10 p-6 rounded-xl shadow-lg"
            >
              <h2 className="text-4xl font-bold">{counts[i]}</h2>
              <p className="text-lg mt-2 font-medium">{item.label}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CounterSection;
