import React, { useEffect, useState } from "react";

const Home = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("https://restate-mock-api.vercel.app/properties")
      .then(res => res.json())
      .then(data => {
        setProperties(data);
        setLoading(false);
      })
      .catch(error => {
        console.error("API Error:", error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <h2 className="text-center text-xl font-bold mt-10">Loading Properties...</h2>;
  }

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6 text-blue-700">üè† Featured Properties</h1>

      {/* Grid layout */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {properties.map((p) => (
          <div
            key={p.id}
            className="border rounded-lg shadow-lg p-4 hover:scale-105 transition duration-300 bg-white"
          >
            <img
              src={p.image}
              alt={p.title}
              className="rounded-md w-full h-48 object-cover mb-4"
            />

            <h2 className="text-xl font-bold text-gray-800">{p.title}</h2>

            <p className="text-gray-600">üè∑ Type: {p.type}</p>
            <p className="text-gray-600">üõè BHK: {p.bhk}</p>
            <p className="text-gray-600">üìç Location: {p.location}</p>

            <p className="text-green-600 text-lg font-semibold mt-2">‚Çπ {p.price}</p>

            <button className="bg-blue-600 text-white w-full py-2 rounded mt-4 hover:bg-blue-700 transition">
              View Details
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
