import React from "react";
import { useParams } from "react-router-dom";

const properties = [
  {
    id: 1,
    title: "Luxury 2 BHK Apartment",
    price: "$120,000",
    location: "Delhi, India",
    image: "https://images.unsplash.com/photo-1600585154340-be6161a56a0c",
    seller: { name: "Amit Sharma", avatar: "https://i.ibb.co/3mT9xzw/user1.png" },
  },
  {
    id: 2,
    title: "Family House",
    price: "$180,000",
    location: "Mumbai, India",
    image: "https://images.unsplash.com/photo-1600607688969-a5bfcd646154",
    seller: { name: "Shruti Patel", avatar: "https://i.ibb.co/7rnSKkj/user3.png" },
  },
  {
    id: 3,
    title: "Premium Villa",
    price: "$250,000",
    location: "Bangalore, India",
    image: "https://images.unsplash.com/photo-1600585154206-573f47f6a04f",
    seller: { name: "Rahul Verma", avatar: "https://i.ibb.co/5sY4T8f/user4.png" },
  },
  {
    id: 4,
    title: "Modern PG Room",
    price: "$450/month",
    location: "Pune, India",
    image: "https://images.unsplash.com/photo-1600566752355-35792bedcfea",
    seller: { name: "Sneha Gupta", avatar: "https://i.ibb.co/3mT9xzw/user1.png" },
  },
];

const PropertyDetails = () => {
  const { id } = useParams();
  const property = properties.find((p) => p.id === parseInt(id));

  if (!property) return <p className="text-white text-center mt-10">Property not found!</p>;

  return (
    <section className="max-w-4xl mx-auto p-6 mt-10 bg-[#07294bff] rounded-xl">
      {/* Property Image */}
      <div className="w-full h-72 md:h-96 overflow-hidden rounded-xl">
        <img
          src={property.image}
          alt={property.title}
          className="w-full h-full object-cover hover:scale-105 duration-500"
        />
      </div>

      {/* Property Info */}
      <div className="p-4 bg-blue-500/10 backdrop-blur-lg rounded-b-xl border-t border-blue-200/10 mt-4">
        <h1 className="text-2xl md:text-3xl font-bold text-white">{property.title}</h1>
        <p className="text-blue-300 font-bold text-lg mt-2">{property.price}</p>
        <p className="text-gray-200 mt-1">{property.location}</p>

        {/* Seller Info */}
        <div className="flex items-center gap-3 mt-4 bg-blue-500/20 backdrop-blur-md py-2 px-4 rounded-xl border border-blue-300/30 shadow-sm">
          <img
            src={property.seller.avatar}
            alt={property.seller.name}
            className="h-10 w-10 rounded-full border border-blue-300"
          />
          <span className="text-white font-semibold">{property.seller.name}</span>
        </div>
      </div>

      {/* Optional Description */}
      <div className="mt-6 text-white text-sm md:text-base">
        <p>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent sed
          vehicula turpis. Quisque commodo neque in magna feugiat, ac vehicula
          purus pretium. Sed euismod, justo a tristique tincidunt, justo sapien
          iaculis nulla, in facilisis ligula nisi sit amet lorem.
        </p>
      </div>
    </section>
  );
};

export default PropertyDetails;
