import React, { useEffect, useState } from "react";

const Home = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("https://real-estate-api-json.vercel.app/properties")
      .then(res => res.json())
      .then(result => {
        setData(result);
        setLoading(false);
      })
      .catch(err => console.log("API Error:", err));
  }, []);

  if (loading) return <h2 className="text-center text-xl mt-5">Loading...</h2>;

  return (
    <div className="p-6 grid md:grid-cols-3 gap-6">
      {data.map((item) => (
        <div key={item.id} className="border rounded shadow hover:scale-105 p-4">
          <img src={item.image} className="h-48 w-full rounded" alt={item.title} />
          <h2 className="text-xl font-bold mt-3">{item.title}</h2>
          <p>üè° Type: {item.type}</p>
          <p>üõè BHK: {item.bhk}</p>
          <p>üìç {item.location}</p>
          <p className="text-green-600 font-semibold mt-2">‚Çπ {item.price}</p>
          <button className="w-full mt-4 bg-blue-600 text-white py-2 rounded">
            View Details
          </button>
        </div>
      ))}
    </div>
  );
};

export default Home;
