import { useParams } from "react-router-dom";
import { postsData } from './RecentPosts';

function BlogDetails() {
  const { id } = useParams(); // URL se id le raha hai
  const post = postsData.find(p => p.id === Number(id));

  if (!post) return <h1 className="text-white p-10">Post Not Found!</h1>;

  return (
    <div className="p-6 text-white">
      <h1 className="text-3xl font-bold">{post.title}</h1>
      <img src={post.img} className="w-full h-80 object-cover rounded-xl mt-4" />
      <p>{post.description}</p>
    </div>
  );
}

export default BlogDetails;
