import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import { postsData } from "../components/RecentPosts";
import { FaHeart, FaThumbsUp } from "react-icons/fa";

export default function BlogDetails() {
  const { id } = useParams();
  const post = postsData.find((p) => p.id === parseInt(id));

  // Pre-added comments
  const [comments, setComments] = useState([
    {
      user: "John Doe",
      comment: "Amazing post! Thanks for sharing.",
      date: "Nov 30, 2025",
    },
    {
      user: "Sarah Wilson",
      comment: "Beautiful house and great description.",
      date: "Nov 29, 2025",
    },
  ]);

  // New Comment Fields
  const [newComment, setNewComment] = useState("");

  const handleAddComment = () => {
    if (newComment.trim().length === 0) return;

    setComments([
      ...comments,
      {
        user: "Guest User",
        comment: newComment,
        date: new Date().toLocaleDateString(),
      },
    ]);

    setNewComment("");
  };

  if (!post) {
    return (
      <div className="text-center py-20 text-xl font-semibold text-red-500">
        ⚠️ Post not found
      </div>
    );
  }

  return (
    <div className="w-full min-h-screen bg-gradient-to-r from-blue-900 to-purple-700 p-6 text-white">
      <div className="max-w-6xl mx-auto flex flex-col lg:flex-row gap-6">

        {/* ================= LEFT CONTENT ===================== */}
        <div className="lg:w-2/3 bg-white/10 backdrop-blur-lg p-6 rounded-xl shadow-xl border border-white/20">

          {/* Back Button */}
          <Link
            to="/"
            className="inline-block mb-4 text-sm px-4 py-2 bg-white/20 rounded-lg hover:bg-white/30"
          >
            ← Back to Home
          </Link>

          {/* Post Image */}
          <div
            className="w-full h-72 rounded-xl bg-cover bg-center shadow-lg"
            style={{ backgroundImage: `url(${post.img})` }}
          ></div>

          {/* Title */}
          <h1 className="text-3xl font-bold mt-5">{post.title}</h1>

          {/* Profile Info */}
          <div className="flex items-center gap-3 mt-4">
            <img
              src={post.profileImg}
              alt={post.profile}
              className="w-14 h-14 rounded-full border-2 border-white object-cover"
            />

            <div>
              <p className="text-lg font-semibold">{post.profile}</p>
              <p className="text-sm text-white/70">
                {post.date} • {post.comments} comments
              </p>
            </div>
          </div>

          {/* Description */}
          <p className="mt-6 leading-relaxed text-white/90 text-lg">
            {post.description}
          </p>

          {/* Dummy Blog Paragraphs */}
          <p className="mt-4 text-white/80">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis
            dignissim, nisl a luctus tincidunt, justo neque pharetra lorem, at
            auctor urna mauris eget urna.
          </p>

          <p className="mt-4 text-white/80">
            Suspendisse potenti. Integer vel sem sed magna pharetra hendrerit ut
            eu justo. Donec pellentesque aliquet sapien.
          </p>

          {/* Likes */}
          <div className="flex gap-4 mt-8">
            <div className="flex items-center gap-2 bg-white/20 px-4 py-2 rounded-lg">
              <FaHeart className="text-red-400" /> {post.likes}
            </div>

            <div className="flex items-center gap-2 bg-white/20 px-4 py-2 rounded-lg">
              <FaThumbsUp className="text-blue-400" /> {post.thumbs}
            </div>
          </div>

          {/* ================== COMMENTS SECTION ================= */}
          <h2 className="text-2xl font-bold mt-10 mb-4">Comments</h2>

          {/* Comment List */}
          <div className="space-y-4">
            {comments.map((c, i) => (
              <div
                key={i}
                className="bg-white/10 border border-white/20 rounded-lg p-4"
              >
                <p className="font-semibold">{c.user}</p>
                <p className="text-white/80 text-sm">{c.date}</p>
                <p className="mt-2">{c.comment}</p>
              </div>
            ))}
          </div>

          {/* Add Comment Box */}
          <div className="mt-6">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              className="w-full p-3 rounded-lg bg-white/20 border border-white/30 text-white outline-none"
              rows="3"
              placeholder="Write your comment..."
            ></textarea>

            <button
              onClick={handleAddComment}
              className="mt-3 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg"
            >
              Add Comment
            </button>
          </div>
        </div>

        {/* ================== RIGHT SIDEBAR ===================== */}
        <div className="lg:w-1/3 bg-white/10 backdrop-blur-lg p-6 rounded-xl shadow-xl border border-white/20 h-fit">

          <h2 className="text-xl font-bold mb-4">Search Blog</h2>

          <input
            type="text"
            placeholder="Search..."
            className="w-full p-3 bg-white/20 border border-white/30 rounded-lg text-white outline-none"
          />

          <button className="w-full mt-3 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg">
            Search
          </button>

        </div>

      </div>
    </div>
  );
}
