import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const location = useLocation();
  const [isOpen, setIsOpen] = useState(false);

  const menuItems = [
    { label: "‚úèÔ∏è Edit Profile", path: "/edit-profile" },
    { label: "‚≠ê My Favorites", path: "/my-favorites" },
    { label: "üì¢ My Ads", path: "/my-ads" },
    { label: "üõ† Manage Ads", path: "/manage-ads" },
    { label: "üí≥ Payments", path: "/payments" },
    { label: "üì¶ Orders", path: "/orders" },
    { label: "üîí Safety Tips", path: "/safety-tips" },
    { label: "‚öôÔ∏è Settings", path: "/settings" },
  ];

  return (
    <>
      {/* Mobile Toggle Button */}
      <div className="md:hidden px-4 mb-4">
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="w-full bg-blue-700 text-white py-2 rounded-lg"
        >
          {isOpen ? "Close Menu" : "Open Menu"}
        </button>
      </div>

      {/* Sidebar */}
      <div
        className={`fixed top-0 left-0 h-full w-72 bg-[#111827] text-white p-6 shadow-xl border-r border-gray-700 z-50
        transform transition-transform duration-300 ease-in-out
        ${isOpen ? "translate-x-0" : "-translate-x-full"} md:translate-x-0 md:static md:block
        overflow-hidden`} // Prevents sidebar from scrolling
      >
        {/* Profile */}
        <div className="text-center mb-6">
          <img
            src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
            className="w-24 h-24 mx-auto rounded-full border-4 border-white/30"
            alt="Profile"
          />
          <h2 className="text-xl font-semibold mt-3">John Doe</h2>
          <p className="text-white/70 text-sm">john.doe@example.com</p>
        </div>

        {/* Menu and Search Box Container */}
        <div className="overflow-y-auto h-[calc(100vh-250px)]"> {/* Make the sidebar scrollable */}
          {/* Menu */}
          <div className="space-y-2 mb-6">
            {menuItems.map((item, i) => (
              <Link
                key={i}
                to={item.path}
                className={`block px-4 py-2 rounded-lg font-medium transition 
                ${location.pathname === item.path
                  ? "bg-white/20"
                  : "bg-white/10 hover:bg-white/20"
                }`}
                onClick={() => setIsOpen(false)}
              >
                {item.label}
              </Link>
            ))}

            <button className="w-full px-4 py-2 mt-2 rounded-lg text-red-400 bg-red-600/20 hover:bg-red-600/30">
              üö™ Logout
            </button>
          </div>

          {/* Search Box */}
          <div className="bg-[#0E1626] p-5 rounded-xl">
            <label className="font-semibold">WHERE ARE YOU LOOKING FOR?</label>
            <input
              type="text"
              placeholder="Type your location..."
              className="w-full mt-2 p-3 rounded-lg bg-gray-800 border border-gray-600 focus:ring focus:ring-blue-500"
            />

            <label className="block font-semibold mt-4">All Categories</label>
            <select className="w-full p-3 border rounded-lg mt-2 bg-gray-800 text-white">
              <option>Select Category</option>
              <option>Apartments</option>
              <option>Villas</option>
              <option>Office</option>
            </select>

            <button className="w-full mt-4 py-3 rounded-lg font-semibold text-white bg-gradient-to-r from-blue-700 to-blue-400">
              Search
            </button>
          </div>
        </div>
      </div>

      {/* Overlay for Mobile */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/50 z-40 md:hidden"
          onClick={() => setIsOpen(false)}
        ></div>
      )}
    </>
  );
};

export default Sidebar;
