import React, { useRef, useEffect, useState } from "react";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";

const properties = [
  {
    id: 1,
    title: "Luxury 2 BHK Apartment",
    price: "$120,000",
    location: "Delhi, India",
    image: "https://i.ibb.co/xDdK9R5/house-4.jpg",
    seller: { name: "Amit Sharma", avatar: "https://i.ibb.co/3mT9xzw/user1.png" },
  },
  {
    id: 2,
    title: "Family House",
    price: "$180,000",
    location: "Mumbai, India",
    image: "https://i.ibb.co/sK44HnZ/house.jpg",
    seller: { name: "Shruti Patel", avatar: "https://i.ibb.co/7rnSKkj/user3.png" },
  },
  {
    id: 3,
    title: "Premium Villa",
    price: "$250,000",
    location: "Bangalore, India",
    image: "https://i.ibb.co/nRtK6D5/house-2.jpg",
    seller: { name: "Rahul Verma", avatar: "https://i.ibb.co/5sY4T8f/user4.png" },
  },
  {
    id: 4,
    title: "Modern PG Room",
    price: "$450/month",
    location: "Pune, India",
    image: "https://i.ibb.co/2q4JH6X/house-3.jpg",
    seller: { name: "Sneha Gupta", avatar: "https://i.ibb.co/3mT9xzw/user1.png" },
  },
  // duplicate for looping effect
  {
    id: 5,
    title: "Luxury 2 BHK Apartment",
    price: "$120,000",
    location: "Delhi, India",
    image: "https://i.ibb.co/xDdK9R5/house-4.jpg",
    seller: { name: "Amit Sharma", avatar: "https://i.ibb.co/3mT9xzw/user1.png" },
  },
  {
    id: 6,
    title: "Family House",
    price: "$180,000",
    location: "Mumbai, India",
    image: "https://i.ibb.co/sK44HnZ/house.jpg",
    seller: { name: "Shruti Patel", avatar: "https://i.ibb.co/7rnSKkj/user3.png" },
  },
  {
    id: 7,
    title: "Premium Villa",
    price: "$250,000",
    location: "Bangalore, India",
    image: "https://i.ibb.co/nRtK6D5/house-2.jpg",
    seller: { name: "Rahul Verma", avatar: "https://i.ibb.co/5sY4T8f/user4.png" },
  },
  {
    id: 8,
    title: "Modern PG Room",
    price: "$450/month",
    location: "Pune, India",
    image: "https://i.ibb.co/2q4JH6X/house-3.jpg",
    seller: { name: "Sneha Gupta", avatar: "https://i.ibb.co/3mT9xzw/user1.png" },
  },
];

const LatestProperties = () => {
  const sliderRef = useRef(null);
  const [isPaused, setIsPaused] = useState(false);

  const scrollLeft = () => {
    sliderRef.current.scrollBy({ left: -260, behavior: "smooth" });
  };

  const scrollRight = () => {
    sliderRef.current.scrollBy({ left: 260, behavior: "smooth" });
  };

  // ðŸ”„ Auto Scroll
  useEffect(() => {
    const slider = sliderRef.current;
    const interval = setInterval(() => {
      if (!isPaused) {
        slider.scrollBy({ left: 260, behavior: "smooth" });

        // loop effect
        if (
          slider.scrollLeft + slider.offsetWidth >=
          slider.scrollWidth / 2
        ) {
          slider.scrollTo({ left: 0, behavior: "smooth" });
        }
      }
    }, 3500); // 3.5 sec gap

    return () => clearInterval(interval);
  }, [isPaused]);

  return (
    <section className="max-w-[1300px] mx-auto px-4 py-10">
      <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">
        Latest <span className="text-blue-600">Properties</span>
      </h2>

      <div
        className="relative"
        onMouseEnter={() => setIsPaused(true)}
        onMouseLeave={() => setIsPaused(false)}
      >
        {/* Left Button */}
        <button
          onClick={scrollLeft}
          className="absolute left-2 top-1/2 -translate-y-1/2 z-20
          bg-white/30 backdrop-blur-xl border border-white/20 shadow-lg
          p-2 rounded-full hover:scale-110 duration-300 flex"
        >
          <FaChevronLeft className="text-white" size={18} />
        </button>

        {/* Slider */}
        <div
          ref={sliderRef}
          className="flex gap-4 overflow-x-auto scroll-smooth py-4 scrollbar-none"
        >
          {properties.map((p) => (
            <div
              key={p.id}
              className="bg-white shadow-md rounded-xl overflow-hidden 
              min-w-[180px] sm:min-w-[200px] md:min-w-[240px]
              hover:shadow-xl duration-300 flex-shrink-0"
            >
              {/* Image */}
              <div className="h-32 sm:h-36 md:h-40 overflow-hidden">
                <img
                  src={p.image}
                  className="w-full h-full object-cover hover:scale-110 duration-500"
                  alt=""
                />
              </div>

              <div className="p-3 bg-blue-500/20 backdrop-blur-lg rounded-b-xl border-t border-blue-200/10">
                <h3 className="text-[14px] sm:text-[15px] font-semibold text-gray-800">
                  {p.title}
                </h3>

                <p className="text-blue-600 font-bold text-sm mt-1">{p.price}</p>
                <p className="text-gray-500 text-xs">{p.location}</p>

                {/* Seller */}
                <div className="flex items-center gap-2 mt-3 
                  bg-blue-500/20 backdrop-blur-md 
                  py-1.5 px-3 rounded-xl border border-blue-300/30 shadow-sm"
                >
                  <img
                    src={p.seller.avatar}
                    className="h-7 w-7 rounded-full border border-blue-300"
                    alt=""
                  />
                  <span className="text-[12px] sm:text-[13px] text-blue-900 font-semibold">
                    {p.seller.name}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Right Button */}
        <button
          onClick={scrollRight}
          className="absolute right-2 top-1/2 -translate-y-1/2 z-20
          bg-white/30 backdrop-blur-xl border border-white/20 shadow-lg
          p-2 rounded-full hover:scale-110 duration-300 flex"
        >
          <FaChevronRight className="text-white" size={18} />
        </button>
      </div>

      <style>{`
        .scrollbar-none::-webkit-scrollbar {
          display: none;
        }
      `}</style>
    </section>
  );
};

export default LatestProperties;
