import React, { useState } from "react";
import Footer from "../components/Footer";

const SettingsPage = () => {
  const [activeTab, setActiveTab] = useState("general");

  // GENERAL
  const [currency, setCurrency] = useState("USD");
  const [language, setLanguage] = useState("English");
  const [phoneVisible, setPhoneVisible] = useState("everyone");
  const [profileVisible, setProfileVisible] = useState("everyone");

  // CHAT
  const [fontSize, setFontSize] = useState("medium");
  const [chatEnabled, setChatEnabled] = useState(true);
  const [emailNotification, setEmailNotification] = useState(true);
  const [backupChat, setBackupChat] = useState(false);
  const [deleteChatOption, setDeleteChatOption] = useState("deleteRead");

  // ADVANCED
  const [autoAdvance, setAutoAdvance] = useState(false);
  const [colorTheme, setColorTheme] = useState("light");
  const [dashboardTheme, setDashboardTheme] = useState("direct");
  const [commentsApproval, setCommentsApproval] = useState("direct");
  const [systemNotification, setSystemNotification] = useState(true);
  const [mailNotification, setMailNotification] = useState(true);
  const [autoWatchComment, setAutoWatchComment] = useState(false);

  return (
    <div className="min-h-screen bg-[#0a1124] text-white flex flex-col items-center px-4 md:px-0 py-10">

      <div className="flex w-full max-w-7xl gap-6">

        {/* LEFT SIDEBAR */}
        <div className="w-48 bg-[#111827] rounded-xl shadow-lg p-5 flex flex-col gap-4">
          {["general", "chat", "advanced"].map((tab) => (
            <button
              key={tab}
              className={`px-4 py-2 rounded text-left font-medium hover:bg-purple-700 ${
                activeTab === tab ? "bg-purple-600" : ""
              }`}
              onClick={() => setActiveTab(tab)}
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1)}
            </button>
          ))}
        </div>

        {/* RIGHT CONTENT */}
        <div className="flex-1 bg-[#111827] rounded-xl shadow-lg p-6 space-y-6">

          {/* GENERAL */}
          {activeTab === "general" && (
            <div className="space-y-4">
              <h2 className="text-2xl font-semibold">General Settings</h2>

              {/* Currency */}
              <div className="flex flex-col md:flex-row gap-4 md:items-center">
                <label className="w-48">Currency:</label>
                <select
                  className="bg-[#1f2937] text-white p-2 rounded w-full md:w-64"
                  value={currency}
                  onChange={(e) => setCurrency(e.target.value)}
                >
                  <option value="USD">USD</option>
                  <option value="EUR">EUR</option>
                  <option value="INR">INR</option>
                </select>
              </div>

              {/* Language */}
              <div className="flex flex-col md:flex-row gap-4 md:items-center">
                <label className="w-48">Language:</label>
                <select
                  className="bg-[#1f2937] text-white p-2 rounded w-full md:w-64"
                  value={language}
                  onChange={(e) => setLanguage(e.target.value)}
                >
                  <option value="English">English</option>
                  <option value="Spanish">Spanish</option>
                  <option value="Hindi">Hindi</option>
                </select>
              </div>

              {/* Phone */}
              <div className="flex flex-col md:flex-row gap-4 md:items-center">
                <label className="w-48">Phone Number:</label>
                <select
                  className="bg-[#1f2937] text-white p-2 rounded w-full md:w-64"
                  value={phoneVisible}
                  onChange={(e) => setPhoneVisible(e.target.value)}
                >
                  <option value="everyone">Visible to Everyone</option>
                  <option value="customers">Visible to Only My Customers</option>
                </select>
              </div>

              {/* Profile */}
              <div className="flex flex-col md:flex-row gap-4 md:items-center">
                <label className="w-48">Profile:</label>
                <select
                  className="bg-[#1f2937] text-white p-2 rounded w-full md:w-64"
                  value={profileVisible}
                  onChange={(e) => setProfileVisible(e.target.value)}
                >
                  <option value="everyone">Visible to Everyone</option>
                  <option value="customers">Visible to Only My Customers</option>
                </select>
              </div>
            </div>
          )}

          {/* CHAT */}
          {activeTab === "chat" && (
            <div className="space-y-4">
              <h2 className="text-2xl font-semibold">Chat Settings</h2>

              {/* Font Size */}
              <div className="flex flex-col md:flex-row gap-4 md:items-center">
                <label className="w-48">Font Size:</label>
                <select
                  className="bg-[#1f2937] text-white p-2 rounded w-full md:w-64"
                  value={fontSize}
                  onChange={(e) => setFontSize(e.target.value)}
                >
                  <option value="small">Small</option>
                  <option value="medium">Medium</option>
                  <option value="large">Large</option>
                </select>
              </div>

              {/* Toggles */}
              <div className="space-y-2">
                <div className="flex justify-between items-center">
                  <span>Chat Enabled:</span>
                  <input
                    type="checkbox"
                    checked={chatEnabled}
                    onChange={() => setChatEnabled(!chatEnabled)}
                  />
                </div>
                <div className="flex justify-between items-center">
                  <span>Email Notification:</span>
                  <input
                    type="checkbox"
                    checked={emailNotification}
                    onChange={() => setEmailNotification(!emailNotification)}
                  />
                </div>
                <div className="flex justify-between items-center">
                  <span>Backup Chat:</span>
                  <input
                    type="checkbox"
                    checked={backupChat}
                    onChange={() => setBackupChat(!backupChat)}
                  />
                </div>
              </div>

              {/* Delete Chat */}
              <div className="flex flex-col md:flex-row gap-4 md:items-center">
                <label className="w-48">Delete Chat:</label>
                <select
                  className="bg-[#1f2937] text-white p-2 rounded w-full md:w-64"
                  value={deleteChatOption}
                  onChange={(e) => setDeleteChatOption(e.target.value)}
                >
                  <option value="deleteRead">Delete Read Chat</option>
                  <option value="deleteAll">Delete All</option>
                </select>
              </div>
            </div>
          )}

          {/* ADVANCED */}
          {activeTab === "advanced" && (
            <div className="space-y-4">
              <h2 className="text-2xl font-semibold">Advanced Settings</h2>

              {/* Auto Advance */}
              <div className="flex justify-between items-center">
                <span>Auto Advance:</span>
                <input
                  type="checkbox"
                  checked={autoAdvance}
                  onChange={() => setAutoAdvance(!autoAdvance)}
                />
              </div>

              {/* Color Theme */}
              <div className="flex flex-col md:flex-row gap-4 md:items-center">
                <label className="w-48">Color Theme:</label>
                <select
                  className="bg-[#1f2937] text-white p-2 rounded w-full md:w-64"
                  value={colorTheme}
                  onChange={(e) => setColorTheme(e.target.value)}
                >
                  <option value="light">Light</option>
                  <option value="dark">Dark</option>
                </select>
              </div>

              {/* Dashboard Theme */}
              <div className="flex flex-col md:flex-row gap-4 md:items-center">
                <label className="w-48">Dashboard Theme:</label>
                <select
                  className="bg-[#1f2937] text-white p-2 rounded w-full md:w-64"
                  value={dashboardTheme}
                  onChange={(e) => setDashboardTheme(e.target.value)}
                >
                  <option value="direct">Posted Ad Directly</option>
                  <option value="adminConfirm">Admin Confirmation</option>
                </select>
              </div>

              {/* Comments */}
              <div className="flex flex-col md:flex-row gap-4 md:items-center">
                <label className="w-48">Comments:</label>
                <select
                  className="bg-[#1f2937] text-white p-2 rounded w-full md:w-64"
                  value={commentsApproval}
                  onChange={(e) => setCommentsApproval(e.target.value)}
                >
                  <option value="direct">Directly</option>
                  <option value="adminConfirm">Admin Confirmation</option>
                </select>
              </div>

              {/* Toggles */}
              <div className="space-y-2">
                <div className="flex justify-between items-center">
                  <span>System Notification:</span>
                  <input
                    type="checkbox"
                    checked={systemNotification}
                    onChange={() =>
                      setSystemNotification(!systemNotification)
                    }
                  />
                </div>

                <div className="flex justify-between items-center">
                  <span>Mail Notification:</span>
                  <input
                    type="checkbox"
                    checked={mailNotification}
                    onChange={() =>
                      setMailNotification(!mailNotification)
                    }
                  />
                </div>

                <div className="flex justify-between items-center">
                  <span>Auto Watch Comment:</span>
                  <input
                    type="checkbox"
                    checked={autoWatchComment}
                    onChange={() =>
                      setAutoWatchComment(!autoWatchComment)
                    }
                  />
                </div>
              </div>
            </div>
          )}

          {/* SAVE & CANCEL */}
          <div className="flex gap-4 mt-6">
            <button className="px-6 py-2 bg-green-600 hover:bg-green-700 rounded font-medium">
              Save Changes
            </button>
            <button className="px-6 py-2 bg-red-600 hover:bg-red-700 rounded font-medium">
              Cancel
            </button>
          </div>
        </div>
      </div>

      {/* FOOTER */}
      <div className="mt-10 w-full max-w-7xl">
        <Footer />
      </div>
    </div>
  );
};

export default SettingsPage;
