import React, { useState } from "react";
import { FaHeart, FaThumbsUp } from "react-icons/fa";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/navigation";
import { Navigation, Autoplay } from "swiper/modules";

const postsData = [
  {
    id: 1,
    title: "Modern Apartment in Germany",
    img: "https://images.unsplash.com/photo-1507089947368-19c1da9775ae?auto=format&fit=crop&w=800&q=80",
    profile: "Emily Johnson",
    profileImg: "https://i.ibb.co/8zPxyzD/woman1.jpg",
    date: "Nov 25, 2025",
    comments: 12,
    likes: 0,
    thumbs: 0,
    description: "Spacious apartment with modern amenities in central Germany.",
  },
  {
    id: 2,
    title: "Luxury Villa in London",
    img: "https://images.unsplash.com/photo-1467269204594-9661b134dd2b?auto=format&fit=crop&w=800&q=80",
    profile: "Michael Brown",
    profileImg: "https://i.ibb.co/TkhFn8c/man1.jpg",
    date: "Nov 24, 2025",
    comments: 8,
    likes: 0,
    thumbs: 0,
    description: "A beautiful villa with garden and private pool in London.",
  },
  {
    id: 3,
    title: "Beach House in Australia",
    img: "https://images.unsplash.com/photo-1506976785307-8732e854ad89?auto=format&fit=crop&w=800&q=80",
    profile: "Sophia Davis",
    profileImg: "https://i.ibb.co/j6qx6Sk/woman2.jpg",
    date: "Nov 23, 2025",
    comments: 5,
    likes: 0,
    thumbs: 0,
    description: "Cozy beach house with stunning sea views in Australia.",
  },
  {
    id: 4,
    title: "Penthouse in Chicago",
    img: "https://images.unsplash.com/photo-1484249170766-998fa6efe3c0?auto=format&fit=crop&w=800&q=80",
    profile: "John Doe",
    profileImg: "https://i.ibb.co/TkhFn8c/man1.jpg",
    date: "Nov 22, 2025",
    comments: 7,
    likes: 0,
    thumbs: 0,
    description: "Luxury penthouse with amazing city view in Chicago.",
  },
  {
    id: 2,
    title: "Luxury Villa in London",
    img: "https://images.unsplash.com/photo-1467269204594-9661b134dd2b?auto=format&fit=crop&w=800&q=80",
    profile: "Michael Brown",
    profileImg: "https://i.ibb.co/TkhFn8c/man1.jpg",
    date: "Nov 24, 2025",
    comments: 8,
    likes: 0,
    thumbs: 0,
    description: "A beautiful villa with garden and private pool in London.",
  },
  {
    id: 3,
    title: "Beach House in Australia",
    img: "https://images.unsplash.com/photo-1506976785307-8732e854ad89?auto=format&fit=crop&w=800&q=80",
    profile: "Sophia Davis",
    profileImg: "https://i.ibb.co/j6qx6Sk/woman2.jpg",
    date: "Nov 23, 2025",
    comments: 5,
    likes: 0,
    thumbs: 0,
    description: "Cozy beach house with stunning sea views in Australia.",
  },
  {
    id: 4,
    title: "Penthouse in Chicago",
    img: "https://images.unsplash.com/photo-1484249170766-998fa6efe3c0?auto=format&fit=crop&w=800&q=80",
    profile: "John Doe",
    profileImg: "https://i.ibb.co/TkhFn8c/man1.jpg",
    date: "Nov 22, 2025",
    comments: 7,
    likes: 0,
    thumbs: 0,
    description: "Luxury penthouse with amazing city view in Chicago.",
  },
];

export default function RecentPosts() {
  const [posts, setPosts] = useState(postsData);

  const handleLike = (id) => {
    setPosts((prev) =>
      prev.map((post) =>
        post.id === id ? { ...post, likes: post.likes + 1 } : post
      )
    );
  };

  const handleThumb = (id) => {
    setPosts((prev) =>
      prev.map((post) =>
        post.id === id ? { ...post, thumbs: post.thumbs + 1 } : post
      )
    );
  };

  return (
    <div className="w-full bg-gray-100 py-10 px-4">
      <h2 className="text-3xl font-bold mb-8 text-center">Recent Posts</h2>

      <Swiper
        modules={[Navigation, Autoplay]}
        spaceBetween={20}
        slidesPerView={1}
        navigation
        autoplay={{ delay: 4000, disableOnInteraction: false }}
        breakpoints={{
          640: { slidesPerView: 1 },
          768: { slidesPerView: 2 },
          1024: { slidesPerView: 4 },
        }}
      >
        {posts.map((post) => (
          <SwiperSlide key={post.id}>
            <div className="flex flex-col rounded-2xl overflow-hidden shadow-lg hover:scale-[1.02] transition bg-white">
              
              {/* Image */}
              <div
                className="h-48 bg-cover bg-center relative"
                style={{ backgroundImage: `url(${post.img})` }}
              >
                <div className="absolute inset-0 bg-black/30"></div>
              </div>

              {/* Content Below Image */}
              <div className="p-4 flex flex-col justify-between h-full">
                <h3 className="text-lg font-bold mb-1">{post.title}</h3>
                <p className="text-sm text-gray-700 mb-2">{post.description}</p>

                {/* Profile + Date */}
                <div className="flex items-center justify-between mt-2">
                  <div className="flex items-center gap-2">
                    <img
                      src={post.profileImg}
                      alt={post.profile}
                      className="w-8 h-8 rounded-full border-2 border-gray-300"
                    />
                    <div>
                      <p className="text-sm font-semibold">{post.profile}</p>
                      <p className="text-xs text-gray-500">{post.date} â€¢ {post.comments} comments</p>
                    </div>
                  </div>

                  {/* Like / Thumb Buttons */}
                  <div className="flex gap-2">
                    <button
                      onClick={() => handleLike(post.id)}
                      className="flex items-center gap-1 bg-gray-100 px-2 py-1 rounded-lg hover:bg-gray-200 transition text-sm"
                    >
                      <FaHeart className="text-red-500 w-4 h-4" /> {post.likes}
                    </button>
                    <button
                      onClick={() => handleThumb(post.id)}
                      className="flex items-center gap-1 bg-gray-100 px-2 py-1 rounded-lg hover:bg-gray-200 transition text-sm"
                    >
                      <FaThumbsUp className="text-blue-500 w-4 h-4" /> {post.thumbs}
                    </button>
                  </div>
                </div>
              </div>

            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
}
