// TestimonialsPage.js
import React from "react";

export default function TestimonialsPage() {
  // Sample testimonials data
  const testimonials = [
    {
      id: 1,
      name: "Rahul Sharma",
      role: "Real Estate Agent",
      rating: 5,
      description: "Rahul helped me find my dream house very smoothly. Highly recommended!",
    },
    {
      id: 2,
      name: "Anita Verma",
      role: "Real Estate Agent",
      rating: 4,
      description: "Good service, but communication could be improved.",
    },
    {
      id: 3,
      name: "Vikram Singh",
      role: "Real Estate Agent",
      rating: 5,
      description: "Very professional and knowledgeable. Loved the experience!",
    },
    {
      id: 4,
      name: "Priya Kapoor",
      role: "Real Estate Agent",
      rating: 3,
      description: "The service was okay, but could be faster in response.",
    },
    {
      id: 5,
      name: "Amit Joshi",
      role: "Real Estate Agent",
      rating: 5,
      description: "Amazing experience! Extremely satisfied with the support provided.",
    },
  ];

  // Component to render each testimonial
  const TestimonialCard = ({ testimonial }) => {
    const { name, role, rating, description } = testimonial;

    // Render stars
    const stars = Array.from({ length: 5 }, (_, index) => {
      return index < rating ? "★" : "☆";
    }).join("");

    return (
      <div className="border p-4 rounded-md shadow-md mb-4 bg-white">
        <div className="text-yellow-500 text-lg">{stars}</div>
        <h3 className="font-bold text-lg mt-2">{name}</h3>
        <p className="text-gray-500 mb-2">{role}</p>
        <p className="text-gray-700">{description}</p>
      </div>
    );
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-center">Testimonials</h1>
      {testimonials.map((t) => (
        <TestimonialCard key={t.id} testimonial={t} />
      ))}
    </div>
  );
}
