// TestimonialsPage.js
import React from "react";

// Sample testimonials with profile pictures
const testimonials = [
  {
    id: 1,
    name: "Rahul Sharma",
    role: "Real Estate Agent",
    rating: 5,
    description: "Rahul helped me find my dream house very smoothly. Highly recommended!",
    profile: "https://randomuser.me/api/portraits/men/32.jpg",
  },
  {
    id: 2,
    name: "Anita Verma",
    role: "Real Estate Agent",
    rating: 4,
    description: "Good service, but communication could be improved.",
    profile: "https://randomuser.me/api/portraits/women/44.jpg",
  },
  {
    id: 3,
    name: "Vikram Singh",
    role: "Real Estate Agent",
    rating: 5,
    description: "Very professional and knowledgeable. Loved the experience!",
    profile: "https://randomuser.me/api/portraits/men/76.jpg",
  },
  {
    id: 4,
    name: "Priya Kapoor",
    role: "Real Estate Agent",
    rating: 3,
    description: "The service was okay, but could be faster in response.",
    profile: "https://randomuser.me/api/portraits/women/55.jpg",
  },
  {
    id: 5,
    name: "Amit Joshi",
    role: "Real Estate Agent",
    rating: 5,
    description: "Amazing experience! Extremely satisfied with the support provided.",
    profile: "https://randomuser.me/api/portraits/men/21.jpg",
  },
];

// Component for individual testimonial card
const TestimonialCard = ({ testimonial }) => {
  const { name, role, rating, description, profile } = testimonial;

  // Stars
  const stars = Array.from({ length: 5 }, (_, i) => (i < rating ? "★" : "☆")).join("");

  return (
    <div className=" border-2 border-gray-200 rounded-lg shadow-lg p-6 flex gap-4 mb-6">
      <img
        src={profile}
        alt={name}
        className="w-16 h-16 rounded-full border-2 border-blue-500 object-cover"
      />
      <div>
        <div className="text-yellow-500 text-lg">{stars}</div>
        <h3 className="font-bold text-lg mt-1">{name}</h3>
        <p className="text-gray-500 mb-2">{role}</p>
        <p className="text-gray-700">{description}</p>
      </div>
    </div>
  );
};

export default function TestimonialsPage() {
  return (
    <div className="min-h-screen bg-gray-100">
      {/* Top Section with background image */}
      <div
        className="w-full h-64 flex items-center justify-center bg-cover bg-center"
        style={{
          backgroundImage:
            "url('https://images.unsplash.com/photo-1503264116251-35a269479413?auto=format&fit=crop&w=1600&q=80')",
        }}
      >
        <h1 className="text-white text-4xl font-bold bg-black/50 px-4 py-2 rounded">
          Testimonial Details
        </h1>
      </div>

      {/* Testimonials Section */}
      <div className="max-w-3xl mx-auto p-6">
        {testimonials.map((t) => (
          <TestimonialCard key={t.id} testimonial={t} />
        ))}
      </div>
    </div>
  );
}
