// ◀ Left Button
  const slideLeft = () => {
    if (sliderRef.current) sliderRef.current.scrollBy({ left: -220, behavior: "smooth" });
  };

  // ▶ Right Button
  const slideRight = () => {
    if (sliderRef.current) sliderRef.current.scrollBy({ left: 220, behavior: "smooth" });
  };
