import React from "react";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";

const SafetyTipsPage = () => {
  return (
    <div className="min-h-screen bg-[#0f172a] text-white flex flex-col">

      {/* -------- MOBILE SIDEBAR TOP (HORIZONTAL SCROLL) -------- */}
      <div className="flex md:hidden overflow-x-auto w-full bg-[#111827] p-2 mb-4">
        <div className="flex gap-4">
          <Sidebar isMobile />
        </div>
      </div>

      {/* -------- MAIN LAYOUT -------- */}
      <div className="flex flex-1">

        {/* -------- LEFT SIDEBAR (DESKTOP) -------- */}
        <div className="hidden md:block w-64 bg-[#111827] p-4">
          <Sidebar />
        </div>

        {/* -------- RIGHT CONTENT -------- */}
        <div className="flex-1 p-4 md:p-8 space-y-8 max-w-4xl">

          {/* ---------- SECTION TEMPLATE (REUSABLE) ---------- */}
          {[
            {
              title: "Watch out for email scams",
              desc: "Be cautious of emails asking for personal information. Scammers often pretend to be trusted companies. Always double-check before clicking links."
            },
            {
              title: "Pay with a credit card",
              desc: "Credit cards offer better fraud protection than debit cards or cash payments. Always choose secure payment methods."
            },
            {
              title: "Check your statements",
              desc: "Regularly review your bank and credit card statements to detect any unauthorized transactions early."
            },
            {
              title: "Report the company",
              desc: "If a company seems suspicious or has scammed you, report it immediately. This helps protect others and alerts authorities."
            }
          ].map((item, index) => (
            <div
              key={index}
              className="bg-[#111827] p-6 rounded-xl shadow-lg border border-gray-700 space-y-4"
            >
              {/* Header */}
              <div className="flex justify-between items-center">
                <h2 className="text-xl md:text-2xl font-semibold">{item.title}</h2>
                <button className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded">
                  Edit
                </button>
              </div>

              {/* Divider */}
              <div className="w-full h-px bg-gray-700"></div>

              {/* Description */}
              <p className="text-gray-300">{item.desc}</p>

              {/* View more */}
              <button className="text-blue-400 hover:text-blue-300 underline">
                View More
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* -------- FOOTER -------- */}
      <Footer />
    </div>
  );
};

export default SafetyTipsPage;
