import React, { useState } from "react";

export default function PropertyFilter() {
  const properties = [
    {
      id: 1,
      type: "apartment",
      title: "Modern 2 BHK Apartment",
      price: "$120,000",
      location: "Delhi, India",
      image:
        "https://images.unsplash.com/photo-1580587771525-78b9dba3b914?auto=format&fit=crop&w=1200&q=80",
    },
    {
      id: 2,
      type: "villa",
      title: "Premium Luxury Villa",
      price: "$350,000",
      location: "Goa, India",
      image:
        "https://images.unsplash.com/photo-1600585154526-990dced4df0d?auto=format&fit=crop&w=1200&q=80",
    },
    {
      id: 3,
      type: "apartment",
      title: "Family Apartment",
      price: "$95,000",
      location: "Bangalore, India",
      image:
        "https://images.unsplash.com/photo-1507089947368-19c1da9775ae?auto=format&fit=crop&w=1200&q=80",
    },
  ];

  const categories = [
    { label: "All", value: "all" },
    { label: "2 BHK Homes", value: "2bhk" },
    { label: "Villas", value: "villa" },
    { label: "Apartments", value: "apartment" },
  ];

  const [filter, setFilter] = useState("all");

  const filteredData =
    filter === "all"
      ? properties
      : properties.filter((p) => p.type === filter);

  return (
    <div className="p-4 max-w-[1200px] mx-auto">
      {/* Filter Buttons */}
      <div className="flex justify-center flex-wrap gap-3 mb-6">
        {categories.map((c) => (
          <button
            key={c.value}
            onClick={() => setFilter(c.value)}
            className={`
              px-4 py-2 rounded-full border font-semibold duration-300
              ${filter === c.value
                ? "bg-[#07294bff] text-white border-[#07294bff]"
                : "bg-[#0a3a6b] text-white border-[#0a3a6b]"}
              hover:bg-[#051f3d] focus:bg-[#051f3d] focus:outline-none
            `}
          >
            {c.label}
          </button>
        ))}
      </div>

      {/* Property Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredData.map((p) => (
          <div
            key={p.id}
            className="bg-[#07294bff] rounded-xl shadow-md overflow-hidden hover:shadow-xl duration-300"
          >
            {/* Image */}
            <img
              src={p.image}
              className="w-full h-48 object-cover"
              alt={p.title}
            />

            {/* Bottom Section with Clear Bold Border */}
            <div className="p-4 border-t-4 border-white/80 text-white">
              <h3 className="text-lg font-semibold">{p.title}</h3>
              <p className="font-bold mt-1">{p.price}</p>
              <p className="text-sm">{p.location}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
