import React, { useState } from "react";

const ForgotPassword = () => {
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Password reset link sent to: " + email);
  };

  return (
    <div className="min-h-screen flex flex-col justify-between bg-gradient-to-br from-[#0A2540] to-[#1A3858]">

      {/* -------- MAIN CONTENT -------- */}
      <div className="flex items-center justify-center flex-grow px-4">
        <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">

          <h2 className="text-2xl font-bold text-center mb-4 text-[#0A2540]">
            Forgot Password
          </h2>

          <p className="text-gray-600 text-center mb-4">
            Enter your email to receive a password reset link.
          </p>

          <form onSubmit={handleSubmit}>
            <input
              type="email"
              required
              placeholder="Enter your email"
              className="w-full border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              onChange={(e) => setEmail(e.target.value)}
            />

            <button
              type="submit"
              className="mt-4 w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-lg shadow"
            >
              Send Reset Link
            </button>
          </form>
        </div>
      </div>

      <Footer />

    </div>
  );
};

export default ForgotPassword;
