import React, { useState } from "react";
import { Link } from "react-router-dom";

const Header = () => {
  const [openDropdown, setOpenDropdown] = useState(null);

  return (
    <header className="bg-gradient-to-r from-[#0A2540] to-[#1A3858] text-white fixed top-0 w-full z-50 shadow-xl">
      <div className="max-w-[1400px] mx-auto p py-4 flex justify-between items-center">

        {/* Logo */}
        <Link to="/" className="text-2xl font-bold text-blue-400 tracking-wide">
          üè† RealEstatePro
        </Link>

        {/* Navigation */}
        <nav className="hidden md:flex items-center gap-8 font-medium">

          <Link to="/" className="hover:text-blue-400 transition">Home</Link>
          <Link to="/about" className="hover:text-blue-400 transition">About Us</Link>

          {/* Widgets Dropdown */}
          <div 
            className="relative cursor-pointer"
            onMouseEnter={() => setOpenDropdown("widgets")}
            onMouseLeave={() => setOpenDropdown(null)}
          >
            <span className="hover:text-blue-400 transition">Widgets ‚ñº</span>

            {openDropdown === "widgets" && (
              <div className="absolute left-0 bg-white text-black mt-2 rounded-lg shadow-lg w-48 py-2 animate-fade">
                <Link className="block px-4 py-2 hover:bg-gray-100">Gallery</Link>
                <Link className="block px-4 py-2 hover:bg-gray-100">Maps</Link>
                <Link className="block px-4 py-2 hover:bg-gray-100">Testimonials</Link>
              </div>
            )}
          </div>

          {/* Blogs Dropdown */}
          <div 
            className="relative cursor-pointer"
            onMouseEnter={() => setOpenDropdown("blogs")}
            onMouseLeave={() => setOpenDropdown(null)}
          >
            <span className="hover:text-blue-400 transition">Blogs ‚ñº</span>

            {openDropdown === "blogs" && (
              <div className="absolute left-0 bg-white text-black mt-2 rounded-lg shadow-lg w-48 py-2 animate-fade">
                <Link className="block px-4 py-2 hover:bg-gray-100">Latest News</Link>
                <Link className="block px-4 py-2 hover:bg-gray-100">Market Trends</Link>
                <Link className="block px-4 py-2 hover:bg-gray-100">Tips & Tricks</Link>
              </div>
            )}
          </div>

          {/* Categories Dropdown */}
          <div 
            className="relative cursor-pointer"
            onMouseEnter={() => setOpenDropdown("categories")}
            onMouseLeave={() => setOpenDropdown(null)}
          >
            <span className="hover:text-blue-400 transition">Categories ‚ñº</span>

            {openDropdown === "categories" && (
              <div className="absolute left-0 bg-white text-black mt-2 rounded-lg shadow-lg w-56 py-2 animate-fade">
                <Link className="block px-4 py-2 hover:bg-gray-100">2 BHK Apartments</Link>
                <Link className="block px-4 py-2 hover:bg-gray-100">Luxury Villas</Link>
                <Link className="block px-4 py-2 hover:bg-gray-100">Farm Houses</Link>
                <Link className="block px-4 py-2 hover:bg-gray-100">Office Space</Link>
              </div>
            )}
          </div>

          <Link to="/contact" className="hover:text-blue-400 transition">
            Contact Us
          </Link>
        </nav>

        {/* Post Property Button */}
        <Link
          to="/post-property"
          className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg font-semibold transition"
        >
          + Post Property
        </Link>
      </div>
    </header>
  );
};

export default Header;
