import React from "react";
import { FaBuilding, FaStore, FaBriefcase, FaHome } from "react-icons/fa";
import CategorySlider from "../components/CategorySlider";
import Footer from "../components/Footer";

const Home = () => {
  const categories = [
    { name: "Commercial Land", icon: <FaBuilding size={22} />, count: 12 },
    { name: "Showrooms & Shops", icon: <FaStore size={22} />, count: 18 },
    { name: "Office Rooms", icon: <FaBriefcase size={22} />, count: 9 },
    { name: "Residential", icon: <FaHome size={22} />, count: 25 },
  ];

  return (
<div className="w-full bg-[#0B2C4D]">

      {/* Banner */}
      <div
        className="relative w-screen bg-cover bg-center"
        style={{
          backgroundImage:
            `url("https://images.unsplash.com/photo-1600585154340-be6161a56a0c?auto=format&fit=crop&w=1600&q=80")`,
          height: "75vh",
        }}
      >

        {/* Dark Overlay */}
        <div className="absolute inset-0 bg-black/50"></div>

        {/* Text + Search Box */}
        <div className="relative flex flex-col justify-center items-center h-full text-center px-4">

          <h1 className="text-white text-4xl sm:text-3xl font-bold drop-shadow-lg">
            Your Property, Our Priority
          </h1>

          <p className="text-white text-lg sm:text-xl mt-2 drop-shadow-lg">
            Buy • Sell • Rent • Luxury Estates • Apartments • Farmhouses
          </p>

          {/* Slim Search Bar */}
          <div className="mt-5 bg-white/30 backdrop-blur-md border border-white/40 rounded-xl p-2 flex flex-col md:flex-row items-center gap-4 max-w-[800px] w-[90%] shadow-xl">

           <input
  type="text"
  placeholder="Find my property..."
  className="w-full md:w-[160px] bg-white/30 backdrop-blur-md p-2 text-sm rounded-md border outline-none placeholder-white/80"
/>

<input
  type="text"
  placeholder="Search Location..."
  className="w-full md:w-[160px] bg-white/30 backdrop-blur-md p-2 text-sm rounded-md border outline-none placeholder-white/80"
/>

<input
  type="date"
  className="w-full md:w-[130px] bg-white/30 backdrop-blur-md p-2 text-sm rounded-md border outline-none placeholder-white/80"
/>

<input
  type="date"
  className="w-full md:w-[130px] bg-white/30 backdrop-blur-md p-2 text-sm rounded-md border outline-none placeholder-white/80"
/>

            <button className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded-lg text-sm font-semibold transition-all shadow">
              Search
            </button>
          </div>
        </div>
{/* Category Boxes */}
<div className="mt-[100] w-full px-6">
  <div className="max-w-[1100px] mx-auto grid grid-cols-2 sm:grid-cols-2 md:grid-cols-4 gap-4">
    {categories.map((item, index) => (
      <div
        key={index}
        className="bg-white/25 backdrop-blur-lg border border-white/40 
        rounded-xl px-4 py-3 w-full
        shadow-lg hover:shadow-xl hover:scale-[1.03]
        transition-all duration-300 cursor-pointer
        flex items-center gap-3"
      >
        {/* Icon */}
        <div className="text-blue-300 text-xl">
          {item.icon}
        </div>

        {/* Text + Count */}
        <div className="flex flex-col leading-tight">
          <h3 className="font-semibold text-sm">{item.name}</h3>
          <p className="text-xs opacity-80">{item.count} Properties</p>
        </div>
      </div>
    ))}
  </div>
</div>
 </div>


     

      {/* Space for Floating Boxes */}
      <div className="h-[70px] h-col-172"></div>

      {/* Category Slider */}
      <CategorySlider />
     
    </div>

  );
};

export default Home;
