import React, { useState } from "react";
import { FaChevronLeft, FaChevronRight, FaStar } from "react-icons/fa";

export default function Testimonials() {
  const testimonialsData = [
    {
      id: 1,
      name: "Elizabeth",
      avatar: "https://i.ibb.co/5sY4T8f/user4.png",
      logo: "https://i.ibb.co/3mT9xzw/user1.png",
      rating: 5,
      description:
        "This real estate service is truly outstanding. They guided me perfectly and helped me find the best property within my budget.",
    },
    {
      id: 2,
      name: "Michael",
      avatar: "https://i.ibb.co/7rnSKkj/user3.png",
      logo: "https://i.ibb.co/3mT9xzw/user1.png",
      rating: 4,
      description:
        "Amazing experience! The team was extremely supportive throughout the buying process. Highly recommended!",
    },
    {
      id: 3,
      name: "Ariana",
      avatar: "https://i.ibb.co/3mT9xzw/user1.png",
      logo: "https://i.ibb.co/5sY4T8f/user4.png",
      rating: 5,
      description:
        "Super smooth process from enquiry to final purchase. Loved their professionalism and quick service.",
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prev) => (prev + 1) % testimonialsData.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prev) =>
      prev === 0 ? testimonialsData.length - 1 : prev - 1
    );
  };

  return (
    <div
      className="relative w-full bg-cover bg-center py-16"
      style={{
        backgroundImage:
          `linear-gradient(rgba(0,0,0,0.65), rgba(0,0,0,0.75)), url("https://images.unsplash.com/photo-1600585154526-990dced4df0d?auto=format&fit=crop&w=1600&q=80")`,
      }}
    >
      {/* Heading */}
      <h2 className="text-center text-4xl font-bold text-white mb-12">
        Testimonials
      </h2>

      {/* Slider */}
      <div className="relative flex items-center justify-center mb-10">
        {/* Left Button */}
        <button
          onClick={prevSlide}
          className="absolute left-4 sm:left-16 bg-[#07294b] text-white p-3 rounded-full shadow-lg hover:bg-black transition"
        >
          <FaChevronLeft size={22} />
        </button>

        {/* Slides */}
        <div className="flex gap-4 overflow-hidden w-full justify-center">
          {testimonialsData.map((t, idx) => {
            const isActive = idx === currentIndex;

            return (
              <div
                key={t.id}
                className={`flex-shrink-0 max-w-sm p-6 rounded-xl shadow-xl backdrop-blur-lg transform transition-all duration-500
                  ${
                    isActive
                      ? "bg-white/90 scale-105 z-20 shadow-2xl"
                      : "bg-white/60 scale-75 opacity-40 z-0"
                  }
                `}
              >
                {/* Logo */}
                <div className="flex justify-center mb-4">
                  <img
                    src={t.logo}
                    alt="Company Logo"
                    className="h-12 object-contain rounded-full"
                  />
                </div>

                {/* Avatar + Name + Rating */}
                <div className="flex items-center gap-3 mb-3 justify-center">
                  <img
                    src={t.avatar}
                    alt={t.name}
                    className="h-14 w-14 rounded-full border-2 border-blue-500"
                  />
                  <div>
                    <h3 className="font-semibold text-gray-800 text-lg">
                      {t.name}
                    </h3>
                    <div className="flex text-yellow-400">
                      {Array.from({ length: t.rating }).map((_, i) => (
                        <FaStar key={i} />
                      ))}
                    </div>
                  </div>
                </div>

                {/* Description */}
                <p className="text-gray-700 text-center text-sm">{t.description}</p>
              </div>
            );
          })}
        </div>

        {/* Right Button */}
        <button
          onClick={nextSlide}
          className="absolute right-4 sm:right-16 bg-[#07294b] text-white p-3 rounded-full shadow-lg hover:bg-black transition"
        >
          <FaChevronRight size={22} />
        </button>
      </div>

      {/* View All Testimonials Button */}
      <div className="text-center">
        <a
          href="/testimonials"
          className="bg-purple-600 hover:bg-purple-700 text-white py-3 px-6 rounded-lg shadow-xl transition"
        >
          View all Testimonials
        </a>
      </div>
    </div>
  );
}
