import React, { useState } from "react";
import Sidebar from "../components/Sidebar";
import { Edit, Trash2, Eye, Heart } from "lucide-react";

const MyAds = () => {
  const [activeFilter, setActiveFilter] = useState("ALL ADS");

  const filters = ["ALL ADS", "PUBLISHED", "FEATURED", "SOLD", "ACTIVE", "EXPIRED"];

  const ads = [
    {
      id: 1,
      name: "Luxury Villa",
      category: "Homes",
      price: "$12,500",
      status: "Active",
      img: "https://images.unsplash.com/photo-1505693416388-ac5ce068fe85",
    },
    {
      id: 2,
      name: "Modern Apartment",
      category: "Apartment",
      price: "$8,200",
      status: "Sold",
      img: "https://images.unsplash.com/photo-1600585154340-be6161a56a0c",
    },
  ];

  // ⭐ FILTER LOGIC ⭐
  const filteredAds = ads.filter((ad) => {
    if (activeFilter === "ALL ADS") return true;
    return ad.status.toUpperCase() === activeFilter;
  });

  return (
    <div className="min-h-screen bg-[#0a1124] text-white flex">
      <Sidebar />

      <div className="flex-1 p-4 md:p-6">
        <h1 className="text-2xl md:text-3xl font-bold mb-6">My Ads</h1>

        {/* FILTER BUTTONS */}
        <div className="flex gap-3 flex-wrap mb-6">
          {filters.map((f) => (
            <button
              key={f}
              onClick={() => setActiveFilter(f)}
              className={`px-3 md:px-4 py-2 text-sm rounded-lg border transition ${
                activeFilter === f
                  ? "bg-blue-600 border-blue-400"
                  : "border-gray-600 hover:bg-gray-700"
              }`}
            >
              {f}
            </button>
          ))}
        </div>

        {/* MOBILE CARD VIEW */}
        <div className="grid sm:hidden gap-4">
          {filteredAds.map((ad) => (
            <div
              key={ad.id}
              className="bg-[#111827] p-4 rounded-xl shadow-lg border border-gray-700"
            >
              <div className="flex items-center gap-4">
                <img
                  src={ad.img}
                  className="w-20 h-20 rounded-lg object-cover"
                  alt={ad.name}
                />
                <div className="flex-1">
                  <p className="font-bold text-lg">{ad.name}</p>
                  <p className="text-gray-400 text-sm">{ad.category}</p>
                  <p className="text-blue-400 font-semibold">{ad.price}</p>

                  <span
                    className={`mt-2 inline-block px-3 py-1 rounded text-xs ${
                      ad.status === "Active"
                        ? "bg-green-700"
                        : ad.status === "Sold"
                        ? "bg-red-700"
                        : "bg-gray-600"
                    }`}
                  >
                    {ad.status}
                  </span>
                </div>
              </div>

              <div className="flex justify-between mt-4">
                <button className="hover:text-blue-400"><Edit size={18} /></button>
                <button className="hover:text-red-400"><Trash2 size={18} /></button>
                <button className="hover:text-pink-400"><Heart size={18} /></button>
                <button className="hover:text-green-400"><Eye size={18} /></button>
              </div>
            </div>
          ))}
        </div>

        {/* DESKTOP TABLE VIEW */}
        <div className="hidden sm:block overflow-x-auto bg-[#111827] p-4 rounded-xl shadow-lg">
          <table className="w-full text-left">
            <thead>
              <tr className="border-b border-gray-700 text-gray-300">
                <th className="p-3">Mark</th>
                <th className="p-3">Item</th>
                <th className="p-3">Category</th>
                <th className="p-3">Price</th>
                <th className="p-3">Ad Status</th>
                <th className="p-3">Action</th>
              </tr>
            </thead>

            <tbody>
              {filteredAds.map((ad) => (
                <tr
                  key={ad.id}
                  className="border-t border-gray-700 hover:bg-[#1a2136] transition"
                >
                  <td className="p-3">
                    <input type="checkbox" className="w-4 h-4" />
                  </td>

                  <td className="p-3 flex items-center gap-3">
                    <img
                      src={ad.img}
                      alt={ad.name}
                      className="w-16 h-16 rounded-lg object-cover"
                    />
                    <p className="font-semibold">{ad.name}</p>
                  </td>

                  <td className="p-3">{ad.category}</td>

                  <td className="p-3 font-semibold">{ad.price}</td>

                  <td className="p-3">
                    <span
                      className={`px-3 py-1 rounded text-sm ${
                        ad.status === "Active"
                          ? "bg-green-700"
                          : ad.status === "Sold"
                          ? "bg-red-700"
                          : "bg-gray-600"
                      }`}
                    >
                      {ad.status}
                    </span>
                  </td>

                  <td className="p-3 flex gap-4">
                    <button className="hover:text-blue-400"><Edit size={18} /></button>
                    <button className="hover:text-red-400"><Trash2 size={18} /></button>
                    <button className="hover:text-pink-400"><Heart size={18} /></button>
                    <button className="hover:text-green-400"><Eye size={18} /></button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

      </div>
    </div>
  );
};

export default MyAds;
