import { useState } from "react";
import { Heart } from "lucide-react";

const countryData = [
  {
    name: "Germany",
    img: "https://images.unsplash.com/photo-1503264116251-35a269479413?auto=format&fit=crop&w=800&q=80",
  },
  {
    name: "London",
    img: "https://images.unsplash.com/photo-1467269204594-9661b134dd2b?auto=format&fit=crop&w=800&q=80",
  },
  {
    name: "Australia",
    img: "https://images.unsplash.com/photo-1506976785307-8732e854ad89?auto=format&fit=crop&w=800&q=80",
  },
  {
    name: "Chicago",
    img: "https://images.unsplash.com/photo-1484249170766-998fa6efe3c0?auto=format&fit=crop&w=800&q=80",
  },
];

export default function TopListingPlaces() {
  const [likes, setLikes] = useState(0);
  const [activeCountry, setActiveCountry] = useState(countryData[0]);
  const [boxLikes, setBoxLikes] = useState({});

  const handleBoxLike = (country) => {
    setBoxLikes((prev) => ({ ...prev, [country]: (prev[country] || 0) + 1 }));
  };

  return (
    <div className="w-full min-h-screen bg-gray-100 p-6 flex flex-col lg:flex-row gap-6">

        {/* LEFT SIDE (4 IMAGE BOXES) */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-1 gap-4 w-full lg:w-1/4">
            {countryData.map((c, i) => (
            <div
                key={i}
                className="h-40 rounded-2xl bg-cover bg-center shadow relative cursor-pointer"
                style={{ backgroundImage: `url(${c.img})` }}
                onClick={() => setActiveCountry(c)}
            >
                <div className="absolute inset-0 bg-black/40 rounded-2xl"></div>

                {/* COUNTRY NAME */}
                <h3 className="absolute bottom-3 left-3 text-white text-xl font-semibold">
                {c.name}
                </h3>

                {/* ❤️ LIKE BUTTON */}
                <button
                onClick={(e) => {
                    e.stopPropagation();
                    handleBoxLike(c.name);
                }}
                className="absolute top-3 right-3 bg-white/20 backdrop-blur-xl px-3 py-1 rounded-xl border border-white/40 text-white flex items-center gap-1 hover:bg-white/30"
                >
                <Heart className="w-4 h-4" /> {boxLikes[c.name] || 0}
                </button>
            </div>
            ))}
        </div>

        {/* RIGHT SIDE BIG IMAGE */}
        <div
            className="flex-1 h-96 rounded-3xl bg-cover bg-center shadow relative text-white p-6"
            style={{ backgroundImage: `url(${activeCountry.img})` }}
        >
            <div className="absolute inset-0 bg-black/40 rounded-3xl"></div>

            <div className="relative z-10 flex flex-col h-full justify-between">
            <div>
                <h1 className="text-4xl font-bold">
                {activeCountry.name}
                </h1>
                <p className="mt-2 text-lg opacity-90 max-w-md">
                Explore the best real estate opportunities in {activeCountry.name}.
                </p>
            </div>

            {/* BIG HEART BUTTON */}
            <button
                onClick={() => setLikes(likes + 1)}
                className="bg-white/20 backdrop-blur-md border border-white/30 px-5 py-2 rounded-xl flex items-center gap-2 w-max hover:bg-white/30 transition"
            >
                <Heart className="w-5 h-5" /> {likes} Likes
            </button>
            </div>
        </div>
    </div>
  );
}
