import React from "react";

const About = () => {
  return (
    <div className="pt-28 bg-gradient-to-b from-[#0A2540] to-[#132E45] text-white">

      {/* HERO SECTION */}
      <div className="max-w-6xl mx-auto px-6 py-16 text-center">
        <h1 className="text-4xl md:text-5xl font-bold mb-4">
          About <span className="text-blue-400">RealEstatePro</span>
        </h1>
        <p className="text-white/70 max-w-3xl mx-auto text-lg">
          We help people find their dream homes by connecting them with trusted real estate options across the country.
        </p>
      </div>

      {/* STATS SECTION */}
      <div className="max-w-6xl mx-auto px-6 grid grid-cols-2 md:grid-cols-4 gap-6 py-12">
        {[
          { number: "10K+", label: "Happy Clients" },
          { number: "5K+", label: "Properties Listed" },
          { number: "120+", label: "Cities Covered" },
          { number: "15+", label: "Years Experience" },
        ].map((stat, i) => (
          <div
            key={i}
            className="bg-white/10 backdrop-blur-lg rounded-2xl text-center p-6 border border-white/10"
          >
            <h2 className="text-3xl font-bold text-blue-400">{stat.number}</h2>
            <p className="text-white/70 mt-1">{stat.label}</p>
          </div>
        ))}
      </div>

      {/* MISSION & VISION */}
      <div className="max-w-6xl mx-auto px-6 py-16 grid md:grid-cols-2 gap-10">
        <div className="bg-white/10 p-8 rounded-2xl border border-white/10 backdrop-blur-lg">
          <h3 className="text-2xl font-bold mb-3 text-blue-300">Our Mission</h3>
          <p className="text-white/70 leading-relaxed">
            Our mission is to simplify the real estate journey for buyers and sellers by offering verified listings, advanced tools, and expert support.
          </p>
        </div>

        <div className="bg-white/10 p-8 rounded-2xl border border-white/10 backdrop-blur-lg">
          <h3 className="text-2xl font-bold mb-3 text-blue-300">Our Vision</h3>
          <p className="text-white/70 leading-relaxed">
            To become the most trusted real estate platform by delivering accuracy, transparency, and a modern property search experience.
          </p>
        </div>
      </div>

      {/* TEAM SECTION */}
      <div className="max-w-6xl mx-auto px-6 py-16">
        <h2 className="text-3xl font-bold text-center mb-10">Our Team</h2>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {[
            {
              name: "Amit Sharma",
              role: "CEO & Founder",
              img: "https://randomuser.me/api/portraits/men/11.jpg",
            },
            {
              name: "Pooja Verma",
              role: "Marketing Head",
              img: "https://randomuser.me/api/portraits/women/47.jpg",
            },
            {
              name: "Rahul Gupta",
              role: "Lead Developer",
              img: "https://randomuser.me/api/portraits/men/32.jpg",
            },
          ].map((team, i) => (
            <div
              key={i}
              className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 text-center border border-white/10"
            >
              <img
                src={team.img}
                alt={team.name}
                className="w-24 h-24 rounded-full mx-auto border-4 border-blue-400 shadow-lg mb-4"
              />
              <h3 className="text-xl font-semibold">{team.name}</h3>
              <p className="text-white/60">{team.role}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default About;
