import React, { useState } from "react";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";

const Order = () => {
  const allOrders = [
    { id: "ORD-1001", category: "Electronics", date: "2025-11-01", price: "$199", status: "Pending" },
    { id: "ORD-1002", category: "Clothing", date: "2025-11-02", price: "$49", status: "Completed" },
    { id: "ORD-1003", category: "Books", date: "2025-11-03", price: "$29", status: "Shipped" },
    { id: "ORD-1004", category: "Furniture", date: "2025-11-04", price: "$499", status: "Pending" },
    { id: "ORD-1005", category: "Toys", date: "2025-11-05", price: "$59", status: "Cancelled" },
    { id: "ORD-1006", category: "Groceries", date: "2025-11-06", price: "$79", status: "Completed" },
    { id: "ORD-1007", category: "Sports", date: "2025-11-07", price: "$120", status: "Shipped" },
    { id: "ORD-1008", category: "Electronics", date: "2025-11-08", price: "$299", status: "Pending" },
    { id: "ORD-1009", category: "Clothing", date: "2025-11-09", price: "$89", status: "Completed" },
    { id: "ORD-1010", category: "Books", date: "2025-11-10", price: "$39", status: "Shipped" },
  ];

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const totalPages = Math.ceil(allOrders.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const currentOrders = allOrders.slice(startIndex, startIndex + itemsPerPage);

  const handlePrev = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  const handleNext = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };

  return (
    <div className="min-h-screen bg-[#0a1124] text-white pt-24 px-4 md:px-0">
      <div className="flex flex-col md:flex-row gap-6 max-w-7xl mx-auto">

        {/* ---------------- DESKTOP SIDEBAR ---------------- */}
        <div className="hidden md:flex flex-col">
          <Sidebar />
        </div>

        {/* ---------------- MOBILE SIDEBAR (SCROLLABLE) ---------------- */}
        <div className="flex md:hidden overflow-x-auto w-full mb-4">
          <div className="flex flex-row gap-4 px-2">
            <Sidebar isMobile />
          </div>
        </div>

        {/* ---------------- RIGHT CONTENT ---------------- */}
        <div className="flex-1 space-y-6">

          <div className="bg-[#111827] p-4 md:p-6 rounded-xl shadow-lg overflow-x-auto">
           <h2 className="text-2xl md:text-3xl font-semibold mb-4">Orders list</h2>

  <table className="min-w-[600px] w-full text-left text-sm md:text-base border-collapse">
    <thead>
      <tr className="bg-[#1b2339] text-gray-300">
        <th className="p-2 md:p-3 border-b border-gray-600">Order ID</th>
        <th className="p-2 md:p-3 border-b border-gray-600">Category</th>
        <th className="p-2 md:p-3 border-b border-gray-600">Date</th>
        <th className="p-2 md:p-3 border-b border-gray-600">Price</th>
        <th className="p-2 md:p-3 border-b border-gray-600">Status</th>
      </tr>
    </thead>
  <tbody>
  {orders.map((order) => (
    <tr key={order.id} className="border-t border-gray-700 hover:bg-[#1a2136] transition">
      <td className="p-2 md:p-3 font-semibold">{order.id}</td>
      <td className="p-2 md:p-3">{order.category}</td>
      <td className="p-2 md:p-3">{order.date}</td>
      <td className="p-2 md:p-3 font-semibold">{order.price}</td>
      <td className="p-2 md:p-3">
        <span className={`px-2 py-1 rounded text-xs md:text-sm font-semibold ${
          order.status === "Completed" ? "bg-green-700" :
          order.status === "Pending" ? "bg-yellow-600" :
          "bg-blue-700"
        }`}>{order.status}</span>
      </td>
    </tr>
  ))}
</tbody>
  </table>
            

            {/* Pagination */}
            <div className="flex justify-end gap-2 mt-4">
              <button
                onClick={handlePrev}
                disabled={currentPage === 1}
                className="px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50"
              >
                Prev
              </button>
              <button
                onClick={handleNext}
                disabled={currentPage === totalPages}
                className="px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded disabled:opacity-50"
              >
                Next
              </button>
            </div>
          </div>

        </div>
      </div>

      {/* ---------------- FOOTER ---------------- */}
      <div className="mt-10 px-4 md:px-0">
        <Footer />
      </div>
    </div>
  );
};

export default Order;
