import React, { useState, useEffect } from "react";
import { FaHeart, FaThumbsUp } from "react-icons/fa";

const postsData = [
  {
    id: 1,
    title: "Modern Apartment in Germany",
    img: "https://images.unsplash.com/photo-1507089947368-19c1da9775ae?auto=format&fit=crop&w=800&q=80",
    profile: "Emily Johnson",
    profileImg: "https://i.ibb.co/8zPxyzD/woman1.jpg",
    date: "Nov 25, 2025",
    comments: 12,
    likes: 0,
    thumbs: 0,
  },
  {
    id: 2,
    title: "Luxury Villa in London",
    img: "https://images.unsplash.com/photo-1467269204594-9661b134dd2b?auto=format&fit=crop&w=800&q=80",
    profile: "Michael Brown",
    profileImg: "https://i.ibb.co/TkhFn8c/man1.jpg",
    date: "Nov 24, 2025",
    comments: 8,
    likes: 0,
    thumbs: 0,
  },
  {
    id: 3,
    title: "Beach House in Australia",
    img: "https://images.unsplash.com/photo-1506976785307-8732e854ad89?auto=format&fit=crop&w=800&q=80",
    profile: "Sophia Davis",
    profileImg: "https://i.ibb.co/j6qx6Sk/woman2.jpg",
    date: "Nov 23, 2025",
    comments: 5,
    likes: 0,
    thumbs: 0,
  },
  {
    id: 4,
    title: "Penthouse in Chicago",
    img: "https://images.unsplash.com/photo-1484249170766-998fa6efe3c0?auto=format&fit=crop&w=800&q=80",
    profile: "John Doe",
    profileImg: "https://i.ibb.co/TkhFn8c/man1.jpg",
    date: "Nov 22, 2025",
    comments: 7,
    likes: 0,
    thumbs: 0,
  },
];

export default function RecentPosts() {
  const [posts, setPosts] = useState(postsData);

  const handleLike = (id) => {
    setPosts((prev) =>
      prev.map((post) =>
        post.id === id ? { ...post, likes: post.likes + 1 } : post
      )
    );
  };

  const handleThumb = (id) => {
    setPosts((prev) =>
      prev.map((post) =>
        post.id === id ? { ...post, thumbs: post.thumbs + 1 } : post
      )
    );
  };

  return (
    <div className="w-full min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <h2 className="text-3xl font-bold mb-8">Recent Posts</h2>

      {/* Slider */}
      <div className="w-full overflow-x-auto flex gap-4 snap-x snap-mandatory scrollbar-hide">
        {posts.map((post) => (
          <div
            key={post.id}
            className="flex-shrink-0 w-[90%] sm:w-[48%] md:w-[23%] h-[300px] bg-cover bg-center rounded-2xl shadow relative snap-start"
            style={{ backgroundImage: `url(${post.img})` }}
          >
            {/* Dark overlay */}
            <div className="absolute inset-0 bg-black/40 rounded-2xl"></div>

            {/* Content */}
            <div className="absolute inset-0 p-4 flex flex-col justify-between text-white z-10">
              <h3 className="font-bold text-lg">{post.title}</h3>
              <div className="flex justify-between items-center mt-2">
                <div className="flex items-center gap-2">
                  <img
                    src={post.profileImg}
                    alt={post.profile}
                    className="w-8 h-8 rounded-full border-2 border-white"
                  />
                  <p className="text-sm">{post.profile}</p>
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() => handleLike(post.id)}
                    className="flex items-center gap-1 bg-white/20 px-2 py-1 rounded-lg hover:bg-white/30 transition text-sm"
                  >
                    <FaHeart className="text-red-500 w-4 h-4" /> {post.likes}
                  </button>
                  <button
                    onClick={() => handleThumb(post.id)}
                    className="flex items-center gap-1 bg-white/20 px-2 py-1 rounded-lg hover:bg-white/30 transition text-sm"
                  >
                    <FaThumbsUp className="text-blue-400 w-4 h-4" /> {post.thumbs}
                  </button>
                </div>
              </div>
              <p className="text-xs mt-1">{post.date} â€¢ {post.comments} comments</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
