import React, { useState, useEffect } from "react";
import { FaChevronLeft, FaChevronRight, FaStar } from "react-icons/fa";
import { useNavigate } from "react-router-dom";

const testimonialsData = [
  {
    id: 1,
    name: "Amit Sharma",
    role: "Customer",
    rating: 5,
    description:
      "This is the best real estate service I've ever used. The team was very professional and helped me find my dream home.",
    avatar: "https://randomuser.me/api/portraits/men/32.jpg",
    logo: "https://upload.wikimedia.org/wikipedia/commons/a/ab/Logo_TV_2015.png",
  },
  {
    id: 2,
    name: "Shruti Patel",
    role: "Investor",
    rating: 4,
    description:
      "Great experience! Found multiple properties with ease. Highly recommend their services.",
    avatar: "https://randomuser.me/api/portraits/women/44.jpg",
    logo: "https://upload.wikimedia.org/wikipedia/commons/a/ab/Logo_TV_2015.png",
  },
  {
    id: 3,
    name: "Rahul Verma",
    role: "Homeowner",
    rating: 5,
    description:
      "Professional and trustworthy. They guided me through every step of buying my villa.",
    avatar: "https://randomuser.me/api/portraits/men/65.jpg",
    logo: "https://upload.wikimedia.org/wikipedia/commons/a/ab/Logo_TV_2015.png",
  },
];

const TestimonialsSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const navigate = useNavigate();

  const nextSlide = () => {
    setCurrentIndex((prev) => (prev + 1) % testimonialsData.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prev) =>
      prev === 0 ? testimonialsData.length - 1 : prev - 1
    );
  };

  // Auto Slide
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  return (
    <section
      className="relative py-16 bg-cover bg-center"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1570129477492-45c003edd2be?auto=format&fit=crop&w=1600&q=80')",
      }}
    >
      <div className="absolute inset-0 bg-black/50"></div>
      <div className="relative max-w-6xl mx-auto px-4">
        <h2 className="text-3xl sm:text-4xl font-bold text-white text-center mb-8">
          What Our Clients Say
        </h2>

        {/* Slider */}
        <div className="relative flex items-center mb-6">
          <button
            onClick={prevSlide}
            className="absolute left-0 z-10 bg-white/30 backdrop-blur-xl p-2 rounded-full hover:bg-white/50 text-black"
          >
            <FaChevronLeft size={20} />
          </button>

          <div className="flex overflow-x-auto scrollbar-none gap-6 w-full py-4">
            {testimonialsData.map((t, idx) => (
              <div
                key={t.id}
                className={`flex-shrink-0 w-[80%] sm:w-[60%] md:w-[45%] lg:w-[30%] max-w-sm mx-auto p-6 rounded-xl shadow-lg bg-white/90 backdrop-blur-lg transform transition-transform duration-500 ${
                  idx === currentIndex
                    ? "scale-100"
                    : "scale-90 opacity-50"
                }`}
              >
                <div className="flex justify-center mb-4">
                  <img src={t.logo} alt="Company Logo" className="h-12 object-contain" />
                </div>

                <div className="flex items-center gap-3 mb-3">
                  <img
                    src={t.avatar}
                    alt={t.name}
                    className="h-12 w-12 rounded-full border-2 border-blue-500"
                  />
                  <div>
                    <h3 className="font-semibold text-gray-800">{t.name}</h3>
                    <div className="flex text-yellow-400">
                      {Array.from({ length: t.rating }).map((_, i) => (
                        <FaStar key={i} />
                      ))}
                    </div>
                  </div>
                </div>

                <p className="text-gray-700">{t.description}</p>
              </div>
            ))}
          </div>

          <button
            onClick={nextSlide}
            className="absolute right-0 z-10 bg-white/30 backdrop-blur-xl p-2 rounded-full hover:bg-white/50 text-black"
          >
            <FaChevronRight size={20} />
          </button>
        </div>

        {/* All Testimonials Button */}
        <div className="flex justify-center">
          <button
            onClick={() => navigate("/testimonials")}
            className="px-6 py-2 bg-[#07294bff] text-white font-semibold rounded-full hover:bg-black duration-300"
          >
            All Testimonials
          </button>
        </div>
      </div>

      <style>{`
        .scrollbar-none::-webkit-scrollbar {
          display: none;
        }
      `}</style>
    </section>
  );
};

export default TestimonialsSlider;
