import React, { useRef } from "react";
import {
  FaHome,
  FaBuilding,
  FaHotel,
  FaCity,
  FaTree,
  FaBriefcase,
} from "react-icons/fa";

const CategorySlider = () => {
  const scrollRef = useRef(null);

  const categories = [
    { name: "Kids House", count: 12, icon: <FaHome /> },
    { name: "Budget House", count: 18, icon: <FaBuilding /> },
    { name: "Luxury House", count: 8, icon: <FaHotel /> },
    { name: "Stylish House", count: 14, icon: <FaCity /> },
    { name: "Apartments", count: 22, icon: <FaHome /> },
    { name: "Farmhouse", count: 9, icon: <FaTree /> },
    { name: "Deluxe House", count: 7, icon: <FaHome /> },
    { name: "Nature House", count: 11, icon: <FaTree /> },
    { name: "2 BHK", count: 16, icon: <FaHome /> },
    { name: "3 BHK", count: 10, icon: <FaHome /> },
    { name: "Office Rooms", count: 5, icon: <FaBriefcase /> },
  ];

  const duplicated = [...categories, ...categories];

  // Auto scroll
  const startAutoScroll = () => {
    if (!scrollRef.current) return;
    scrollRef.current.style.animationPlayState = "running";
  };

  const stopAutoScroll = () => {
    if (!scrollRef.current) return;
    scrollRef.current.style.animationPlayState = "paused";
  };

  // Manual buttons
  const slideLeft = () => {
    scrollRef.current.scrollLeft -= 300;
  };

  const slideRight = () => {
    scrollRef.current.scrollLeft += 300;
  };

  return (
    <div className="w-full bg-[#07182E] py-12 relative overflow-hidden">
      <h2 className="text-white text-3xl font-bold text-center mb-6">
        Popular Categories
      </h2>

      {/* BUTTONS */}
      <button
        onClick={slideLeft}
        className="absolute left-2 top-1/2 -translate-y-1/2 bg-white/20 text-white p-3 rounded-full hover:bg-white/40 backdrop-blur-md z-20"
      >
        ❮
      </button>

      <button
        onClick={slideRight}
        className="absolute right-2 top-1/2 -translate-y-1/2 bg-white/20 text-white p-3 rounded-full hover:bg-white/40 backdrop-blur-md z-20"
      >
        ❯
      </button>

      {/* SLIDER */}
      <div
        className="overflow-hidden"
        onMouseEnter={stopAutoScroll}
        onMouseLeave={startAutoScroll}
      >
        <div
          ref={scrollRef}
          className="flex gap-6 animate-loopSlider"
          style={{ width: "max-content" }}
        >
          {duplicated.map((item, index) => (
            <div
              key={index}
              className="min-w-[250px] h-[130px] bg-white/15 backdrop-blur-xl 
              border border-white/20 rounded-2xl shadow-md 
              flex items-center gap-4 p-5 text-white 
              hover:scale-105 transition duration-300 cursor-pointer"
            >
              <div className="text-blue-300 text-3xl">{item.icon}</div>

              <div>
                <h3 className="font-semibold text-lg">{item.name}</h3>
                <p className="text-sm opacity-80">{item.count} available</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* ANIMATION CSS */}
      <style>{`
        .animate-loopSlider {
          animation: autoSlide 25s linear infinite;
        }

        @keyframes autoSlide {
          from { transform: translateX(0); }
          to   { transform: translateX(-50%); }
        }
      `}</style>
    </div>
  );
};

export default CategorySlider;
