import React, { useRef } from "react";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";

const properties = [
  {
    id: 1,
    title: "Luxury 2 BHK Apartment",
    price: "$120,000",
    location: "Delhi, India",
    image: "https://i.ibb.co/xDdK9R5/house-4.jpg",
    seller: {
      name: "Amit Sharma",
      avatar: "https://i.ibb.co/3mT9xzw/user1.png",
    },
  },
  {
    id: 2,
    title: "Modern Family House",
    price: "$180,000",
    location: "Mumbai, India",
    image: "https://i.ibb.co/sK44HnZ/house.jpg",
    seller: {
      name: "Shruti Patel",
      avatar: "https://i.ibb.co/7rnSKkj/user3.png",
    },
  },
  {
    id: 3,
    title: "Luxury Villa",
    price: "$250,000",
    location: "Bangalore, India",
    image: "https://i.ibb.co/nRtK6D5/house-2.jpg",
    seller: {
      name: "Rahul Verma",
      avatar: "https://i.ibb.co/5sY4T8f/user4.png",
    },
  },
  {
    id: 4,
    title: "Modern PG Room",
    price: "$450/month",
    location: "Pune, India",
    image: "https://i.ibb.co/2q4JH6X/house-3.jpg",
    seller: {
      name: "Sneha Gupta",
      avatar: "https://i.ibb.co/3mT9xzw/user1.png",
    },
  },
];

const LatestProperties = () => {
  const sliderRef = useRef(null);

  const scrollLeft = () => {
    sliderRef.current.scrollBy({ left: -300, behavior: "smooth" });
  };

  const scrollRight = () => {
    sliderRef.current.scrollBy({ left: 300, behavior: "smooth" });
  };

  return (
    <section className="max-w-[1300px] mx-auto px-4 py-10">
      {/* Heading */}
      <h2 className="text-3xl font-bold text-center text-gray-800 mb-8">
        Latest <span className="text-blue-600">Properties</span>
      </h2>

      {/* Slider Wrapper */}
      <div className="relative">

        {/* Left Button */}
        <button
          onClick={scrollLeft}
          className="absolute left-0 top-1/2 -translate-y-1/2 z-20 bg-white shadow p-3 rounded-full hover:scale-110 duration-200 hidden md:block"
        >
          <FaChevronLeft size={20} />
        </button>

        {/* Slider */}
        <div
          ref={sliderRef}
          className="flex gap-6 overflow-x-auto scrollbar-none scroll-smooth py-4"
        >
          {properties.map((p) => (
            <div
              key={p.id}
              className="min-w-[260px] bg-white shadow-md rounded-xl overflow-hidden hover:shadow-xl duration-300"
            >
              <img src={p.image} alt="" className="h-40 w-full object-cover" />

              <div className="p-4">
                <h3 className="text-[18px] font-semibold text-gray-800">
                  {p.title}
                </h3>

                <p className="text-blue-600 font-bold mt-1">{p.price}</p>
                <p className="text-gray-500 text-sm">{p.location}</p>

                {/* Seller Box */}
                <div className="flex items-center gap-3 mt-4 bg-gray-100 p-2 rounded-lg">
                  <img
                    src={p.seller.avatar}
                    className="h-10 w-10 rounded-full border"
                    alt=""
                  />

                  <span className="font-medium text-gray-700 text-sm">
                    {p.seller.name}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Right Button */}
        <button
          onClick={scrollRight}
          className="absolute left-0 top-1/2 -translate-y-1/2 z-20 bg-white  p-3 rounded-full hover:scale-110 duration-200 hidden md:block"
        >
          <FaChevronRight size={20} />
        </button>
      </div>
    </section>
  );
};

export default LatestProperties;
