import React, { useState } from "react";
import Footer from "../components/Footer";

const ForgotPassword = () => {
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Password reset link sent to: " + email);
  };

  return (
    <>
      {/* Main Page Wrapper */}
      <div className="min-h-screen bg-gray-100 flex flex-col pb-24 pb-16">

        {/* Center Content */}
        <div className="flex-grow flex items-center justify-center px-">
          <div className="bg-white p-8 rounded-xl shadow-xl w-full max-w-md">

            <h2 className="text-3xl font-bold text-center mb-4 text-[#0A2540]">
              Forgot Password
            </h2>

            <p className="text-gray-600 text-center mb-6">
              Enter your email to receive a password reset link.
            </p>

            <form onSubmit={handleSubmit}>
              <input
                type="email"
                required
                placeholder="Enter your email"
                className="w-full border border-gray-300 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                onChange={(e) => setEmail(e.target.value)}
              />

              <button
                type="submit"
                className="mt-5 w-full bg-blue-600 hover:bg-blue-700 text-white py-2.5 rounded-lg shadow-md transition"
              >
                Send Reset Link
              </button>
            </form>

          </div>
        </div>

        {/* Footer */}
        <Footer />
      </div>
    </>
  );
};

export default ForgotPassword;
