import React, { useState } from "react";

const Home = () => {
  const [location, setLocation] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  return (
    <div
      className="relative w-full bg-cover bg-center flex items-center justify-center"
      style={{
        backgroundImage: `url('YOUR_IMAGE_LINK')`,
        height: "90vh", // üëà ‡§Ø‡§π‡§æ‡§Å ‡§∏‡•á Banner Height ‡§¨‡§¶‡§≤ ‡§∏‡§ï‡§§‡•á ‡§π‡•ã (px, %, vh)
        
      }}
    >
      {/* Overlay */}
      <div className="absolute inset-0 bg-black/55"></div>

      {/* Content */}
      <div className="relative text-center max-w-[1200px] w-full px-4">
        
        {/* Heading */}
        <h1 className="text-white text-4xl md:text-6xl font-bold mb-4">
          Your Property, Our Priority
        </h1>

        <p className="text-white text-lg md:text-xl mb-6 opacity-90">
          Find the best properties for Sell, Rent & Investment.
        </p>

        {/* Search Panel */}
        <div
          className="mx-auto bg-white/20 backdrop-blur-lg border border-white/30 shadow-xl
           rounded-xl flex flex-col md:flex-row items-center gap-4 p-5"
          style={{
            width: "90%", // üëà ‡§Ø‡§π‡§æ‡§Å ‡§∏‡•á complete box ‡§ï‡•Ä ‡§ö‡•å‡§°‡§º‡§æ‡§à change ‡§ï‡§∞ ‡§∏‡§ï‡§§‡•á ‡§π‡•ã
            maxWidth: "950px", // üëà ‡§¨‡§°‡§º‡•Ä ‡§∏‡•ç‡§ï‡•ç‡§∞‡•Ä‡§® ‡§™‡§∞ max width
          }}
        >
          {/* Find Label */}
          <span className="text-white font-semibold bg-blue-600 px-4 py-2 rounded whitespace-nowrap">
            üîç Find My Property
          </span>

          {/* Location */}
          <input
            type="text"
            placeholder="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="p-3 w-full md:w-[200px] rounded bg-white text-black"
          />

          {/* Start Date */}
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="p-3 w-full md:w-[180px] rounded bg-white text-black"
          />

          {/* End Date */}
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            className="p-3 w-full md:w-[180px] rounded bg-white text-black"
          />

          {/* Button */}
          <button className="bg-blue-600 hover:bg-blue-700 font-semibold text-white px-6 py-3 rounded w-full md:w-auto">
            Search
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
