import React from "react";
import { useParams, Link } from "react-router-dom";

const properties = [
  {
    id: 1,
    title: "Luxury 2 BHK Apartment",
    price: "$120,000",
    location: "Delhi, India",
    image: "https://images.unsplash.com/photo-1600585154340-be6161a56a0c",
    seller: { name: "Amit Sharma", avatar: "https://i.ibb.co/3mT9xzw/user1.png" },
  },
  {
    id: 2,
    title: "Family House",
    price: "$180,000",
    location: "Mumbai, India",
    image: "https://images.unsplash.com/photo-1600607688969-a5bfcd646154",
    seller: { name: "Shruti Patel", avatar: "https://i.ibb.co/7rnSKkj/user3.png" },
  },
  {
    id: 3,
    title: "Premium Villa",
    price: "$250,000",
    location: "Bangalore, India",
    image: "https://images.unsplash.com/photo-1600585154206-573f47f6a04f",
    seller: { name: "Rahul Verma", avatar: "https://i.ibb.co/5sY4T8f/user4.png" },
  },
];

export default function PropertiesDetails() {
  const { id } = useParams();
  const property = properties.find((p) => p.id === parseInt(id));

  if (!property) return <div className="text-white p-5">Property not found!</div>;

  return (
    <div className="min-h-screen bg-[#0f1629] text-white p-5">
      <Link to="/" className="text-blue-400 underline mb-4 inline-block">‚Üê Back to Listings</Link>

      <h1 className="text-3xl font-bold mb-4">{property.title}</h1>
      <img src={property.image} alt={property.title} className="w-full h-64 object-cover rounded-xl mb-4" />
      <p className="text-blue-300 font-bold mb-1">Price: {property.price}</p>
      <p className="text-gray-300 mb-2">Location: {property.location}</p>
      <div className="flex items-center gap-3 mt-4 bg-blue-500/20 p-3 rounded-xl border border-blue-300/30">
        <img src={property.seller.avatar} className="h-10 w-10 rounded-full border border-blue-300" alt="" />
        <span>{property.seller.name}</span>
      </div>
    </div>
  );
}
