import React, { useState, useRef, useEffect } from "react";
import { FaChevronLeft, FaChevronRight, FaStar } from "react-icons/fa";

const testimonialsData = [
  {
    id: 1,
    name: "Amit Sharma",
    role: "Customer",
    category: "all",
    rating: 5,
    description:
      "This is the best real estate service I've ever used. The team was very professional and helped me find my dream home.",
    avatar: "https://randomuser.me/api/portraits/men/32.jpg",
    logo: "https://upload.wikimedia.org/wikipedia/commons/a/ab/Logo_TV_2015.png",
  },
  {
    id: 2,
    name: "Shruti Patel",
    role: "Investor",
    category: "investor",
    rating: 4,
    description:
      "Great experience! Found multiple properties with ease. Highly recommend their services.",
    avatar: "https://randomuser.me/api/portraits/women/44.jpg",
    logo: "https://upload.wikimedia.org/wikipedia/commons/a/ab/Logo_TV_2015.png",
  },
  {
    id: 3,
    name: "Rahul Verma",
    role: "Homeowner",
    category: "homeowner",
    rating: 5,
    description:
      "Professional and trustworthy. They guided me through every step of buying my villa.",
    avatar: "https://randomuser.me/api/portraits/men/65.jpg",
    logo: "https://upload.wikimedia.org/wikipedia/commons/a/ab/Logo_TV_2015.png",
  },
  {
    id: 4,
    name: "Sneha Gupta",
    role: "Customer",
    category: "all",
    rating: 5,
    description:
      "Excellent support and guidance throughout. Really satisfied with the service.",
    avatar: "https://randomuser.me/api/portraits/women/68.jpg",
    logo: "https://upload.wikimedia.org/wikipedia/commons/a/ab/Logo_TV_2015.png",
  },
];

const categories = [
  { label: "All", value: "all" },
  { label: "Customer", value: "all" },
  { label: "Investor", value: "investor" },
  { label: "Homeowner", value: "homeowner" },
];

const Testimonials = () => {
  const [filter, setFilter] = useState("all");
  const [current, setCurrent] = useState(0);
  const sliderRef = useRef(null);
  const [isPaused, setIsPaused] = useState(false);

  const filteredData =
    filter === "all"
      ? testimonialsData
      : testimonialsData.filter((t) => t.category === filter);

  const nextSlide = () => {
    setCurrent((prev) => (prev + 1) % filteredData.length);
  };

  const prevSlide = () => {
    setCurrent((prev) =>
      prev === 0 ? filteredData.length - 1 : prev - 1
    );
  };

  // Auto-scroll every 4 sec
  useEffect(() => {
    const interval = setInterval(() => {
      if (!isPaused && filteredData.length > 1) {
        nextSlide();
      }
    }, 4000);
    return () => clearInterval(interval);
  }, [isPaused, filteredData]);

  // Swipe support
  useEffect(() => {
    const slider = sliderRef.current;
    let startX = 0;
    let endX = 0;

    const onTouchStart = (e) => {
      startX = e.touches[0].clientX;
    };
    const onTouchMove = (e) => {
      endX = e.touches[0].clientX;
    };
    const onTouchEnd = () => {
      if (startX - endX > 50) nextSlide();
      if (endX - startX > 50) prevSlide();
    };

    slider.addEventListener("touchstart", onTouchStart);
    slider.addEventListener("touchmove", onTouchMove);
    slider.addEventListener("touchend", onTouchEnd);

    return () => {
      slider.removeEventListener("touchstart", onTouchStart);
      slider.removeEventListener("touchmove", onTouchMove);
      slider.removeEventListener("touchend", onTouchEnd);
    };
  }, [filteredData]);

  return (
    <section
      className="relative py-16 bg-cover bg-center"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1570129477492-45c003edd2be?auto=format&fit=crop&w=1600&q=80')",
      }}
    >
      <div className="absolute inset-0 bg-black/50"></div>
      <div className="relative max-w-6xl mx-auto px-4">
        <h2 className="text-3xl sm:text-4xl font-bold text-white text-center mb-8">
          What Our Clients Say
        </h2>

        {/* Filter Buttons */}
        <div className="flex justify-center flex-wrap gap-3 mb-8">
          {categories.map((c) => (
            <button
              key={c.value}
              onClick={() => {
                setFilter(c.value);
                setCurrent(0);
              }}
              className={`relative px-4 py-2 rounded-full border font-semibold duration-300
                ${filter === c.value
                  ? "bg-[#07294bff] text-white border-white/50"
                  : "bg-[#07294bff] text-white border-white/30"}
                hover:bg-[#0b3a66] focus:bg-[#0b3a66] focus:outline-none`}
            >
              {c.label}
              <span className="absolute inset-0 bg-black/20 opacity-0 active:opacity-100 rounded-full pointer-events-none"></span>
            </button>
          ))}
        </div>

        {/* Slider */}
        {filteredData.length > 0 && (
          <div
            className="relative flex items-center"
            onMouseEnter={() => setIsPaused(true)}
            onMouseLeave={() => setIsPaused(false)}
            ref={sliderRef}
          >
            <button
              onClick={prevSlide}
              className="absolute left-0 z-10 bg-white/30 backdrop-blur-xl p-2 rounded-full hover:bg-white/50 text-black"
            >
              <FaChevronLeft size={20} />
            </button>

            <div className="flex overflow-hidden w-full justify-center">
              {filteredData.map((t, idx) => (
                <div
                  key={t.id}
                  className={`flex-shrink-0 w-full max-w-md mx-4 p-6 rounded-xl shadow-lg bg-white/90 backdrop-blur-lg transform transition-transform duration-500 ${
                    idx === current ? "scale-100" : "scale-90 opacity-50"
                  }`}
                >
                  <div className="flex justify-center mb-4">
                    <img
                      src={t.logo}
                      alt="Company Logo"
                      className="h-12 object-contain"
                    />
                  </div>

                  <div className="flex items-center gap-3 mb-3">
                    <img
                      src={t.avatar}
                      alt={t.name}
                      className="h-12 w-12 rounded-full border-2 border-blue-500"
                    />
                    <div>
                      <h3 className="font-semibold text-gray-800">{t.name}</h3>
                      <div className="flex text-yellow-400">
                        {Array.from({ length: t.rating }).map((_, i) => (
                          <FaStar key={i} />
                        ))}
                      </div>
                    </div>
                  </div>

                  <p className="text-gray-700">{t.description}</p>
                </div>
              ))}
            </div>

            <button
              onClick={nextSlide}
              className="absolute right-0 z-10 bg-white/30 backdrop-blur-xl p-2 rounded-full hover:bg-white/50 text-black"
            >
              <FaChevronRight size={20} />
            </button>
          </div>
        )}
      </div>
    </section>
  );
};

export default Testimonials;
