import React, { useState, useEffect } from "react";
import { FaHeart, FaThumbsUp } from "react-icons/fa";

const postsData = [
  {
    id: 1,
    title: "Modern Apartment in Germany",
    img: "https://images.unsplash.com/photo-1507089947368-19c1da9775ae?auto=format&fit=crop&w=800&q=80",
    profile: "Emily Johnson",
    profileImg: "https://i.ibb.co/8zPxyzD/woman1.jpg",
    date: "Nov 25, 2025",
    comments: 12,
    likes: 0,
    thumbs: 0,
    description: "Spacious apartment with modern amenities in central Germany.",
  },
  {
    id: 2,
    title: "Luxury Villa in London",
    img: "https://images.unsplash.com/photo-1467269204594-9661b134dd2b?auto=format&fit=crop&w=800&q=80",
    profile: "Michael Brown",
    profileImg: "https://i.ibb.co/TkhFn8c/man1.jpg",
    date: "Nov 24, 2025",
    comments: 8,
    likes: 0,
    thumbs: 0,
    description: "A beautiful villa with garden and private pool in London.",
  },
  {
    id: 3,
    title: "Beach House in Australia",
    img: "https://images.unsplash.com/photo-1506976785307-8732e854ad89?auto=format&fit=crop&w=800&q=80",
    profile: "Sophia Davis",
    profileImg: "https://i.ibb.co/j6qx6Sk/woman2.jpg",
    date: "Nov 23, 2025",
    comments: 5,
    likes: 0,
    thumbs: 0,
    description: "Cozy beach house with stunning sea views in Australia.",
  },
];

export default function RecentPosts() {
  const [index, setIndex] = useState(0);
  const [posts, setPosts] = useState(postsData);

  // Auto slider every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % posts.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [posts.length]);

  // Handle Likes
  const handleLike = (id) => {
    setPosts((prev) =>
      prev.map((post) =>
        post.id === id ? { ...post, likes: post.likes + 1 } : post
      )
    );
  };

  // Handle Thumbs Up
  const handleThumb = (id) => {
    setPosts((prev) =>
      prev.map((post) =>
        post.id === id ? { ...post, thumbs: post.thumbs + 1 } : post
      )
    );
  };

  const activePost = posts[index];

  return (
    <div className="w-full min-h-screen bg-gray-100 flex flex-col items-center p-6">
      
      {/* Section Title */}
      <h2 className="text-3xl font-bold mb-8">Recent Posts</h2>

      {/* Slider Container */}
      <div className="relative w-full max-w-4xl h-[500px] sm:h-[400px] md:h-[450px] rounded-3xl overflow-hidden shadow-lg">
        
        {/* Slide */}
        <div
          key={activePost.id}
          className="w-full h-full bg-cover bg-center relative transition-all duration-700 ease-in-out"
          style={{ backgroundImage: `url(${activePost.img})` }}
        >
          {/* Dark Overlay */}
          <div className="absolute inset-0 bg-black/40 rounded-3xl"></div>

          {/* Content */}
          <div className="absolute inset-0 p-6 flex flex-col justify-between text-white z-10">
            {/* Title + Description */}
            <div>
              <h3 className="text-2xl sm:text-3xl font-bold drop-shadow-lg">
                {activePost.title}
              </h3>
              <p className="mt-2 text-white/90 max-w-md">{activePost.description}</p>
            </div>

            {/* Footer: Profile, Date, Comments, Likes, Thumbs */}
            <div className="flex justify-between items-center mt-4">
              <div className="flex items-center gap-3">
                <img
                  src={activePost.profileImg}
                  alt={activePost.profile}
                  className="w-10 h-10 rounded-full border-2 border-white"
                />
                <div>
                  <p className="font-semibold">{activePost.profile}</p>
                  <p className="text-sm text-white/80">{activePost.date} â€¢ {activePost.comments} comments</p>
                </div>
              </div>

              <div className="flex gap-4">
                <button
                  onClick={() => handleLike(activePost.id)}
                  className="flex items-center gap-1 bg-white/20 px-3 py-1 rounded-lg hover:bg-white/30 transition"
                >
                  <FaHeart className="text-red-500 w-5 h-5" /> {activePost.likes}
                </button>

                <button
                  onClick={() => handleThumb(activePost.id)}
                  className="flex items-center gap-1 bg-white/20 px-3 py-1 rounded-lg hover:bg-white/30 transition"
                >
                  <FaThumbsUp className="text-blue-400 w-5 h-5" /> {activePost.thumbs}
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Previous / Next Buttons */}
        <button
          onClick={() => setIndex(index === 0 ? posts.length - 1 : index - 1)}
          className="absolute left-4 top-1/2 -translate-y-1/2 bg-white/20 p-3 rounded-full hover:bg-white/40 transition"
        >
          &#10094;
        </button>

        <button
          onClick={() => setIndex((index + 1) % posts.length)}
          className="absolute right-4 top-1/2 -translate-y-1/2 bg-white/20 p-3 rounded-full hover:bg-white/40 transition"
        >
          &#10095;
        </button>
      </div>
    </div>
  );
}
