import React, { useState } from "react";
import { Link } from "react-router-dom";

const Header = () => {
  const [openDropdown, setOpenDropdown] = useState(null);

  const toggleDropdown = (menu) => {
    setOpenDropdown(openDropdown === menu ? null : menu);
  };

  return (
    <header className="shadow-md bg-white sticky top-0 z-50">
      <div className="container mx-auto px-6 py-4 flex justify-between items-center">

        {/* Logo */}
        <Link to="/" className="text-2xl font-bold text-blue-600">
          üè† RealEstatePro
        </Link>

        {/* Navigation */}
        <nav className="flex items-center gap-6 font-medium">

          <Link to="/" className="hover:text-blue-600">Home</Link>

          <Link to="/about" className="hover:text-blue-600">About Us</Link>

          {/* Widgets Dropdown */}
          <div className="relative">
            <button
              onClick={() => toggleDropdown("widgets")}
              className="hover:text-blue-600"
            >
              Widgets ‚ñº
            </button>
            {openDropdown === "widgets" && (
              <div className="absolute bg-white border shadow-md rounded p-3 mt-2 w-40">
                <Link className="block hover:text-blue-600 p-1">Gallery</Link>
                <Link className="block hover:text-blue-600 p-1">Maps</Link>
                <Link className="block hover:text-blue-600 p-1">Testimonials</Link>
              </div>
            )}
          </div>

          {/* Blogs Dropdown */}
          <div className="relative">
            <button
              onClick={() => toggleDropdown("blogs")}
              className="hover:text-blue-600"
            >
              Blogs ‚ñº
            </button>
            {openDropdown === "blogs" && (
              <div className="absolute bg-white border shadow-md rounded p-3 mt-2 w-40">
                <Link className="block hover:text-blue-600 p-1">Latest News</Link>
                <Link className="block hover:text-blue-600 p-1">Market Trends</Link>
                <Link className="block hover:text-blue-600 p-1">Tips & Tricks</Link>
              </div>
            )}
          </div>

          {/* Categories Dropdown */}
         <div
  className="relative"
  onMouseEnter={() => setOpenDropdown("categories")}
  onMouseLeave={() => setOpenDropdown(null)}
>
  <button className="hover:text-blue-600">
    Categories ‚ñº
  </button>

  {openDropdown === "categories" && (
    <div className="absolute bg-white border shadow-md rounded p-3 mt-2 w-48 transition-all duration-300">
      <Link className="block hover:text-blue-600 p-1 cursor-pointer">2 BHK Apartments</Link>
      <Link className="block hover:text-blue-600 p-1 cursor-pointer">Luxury Villas</Link>
      <Link className="block hover:text-blue-600 p-1 cursor-pointer">Farm Houses</Link>
      <Link className="block hover:text-blue-600 p-1 cursor-pointer">Office Space</Link>
    </div>
  )}
</div>

          <Link to="/contact" className="hover:text-blue-600">
            Contact Us
          </Link>

          {/* Post Property Button */}
          <Link
            to="/post-property"
            className="bg-blue-600 text-white px-4 py-2 hover:bg-blue-700 rounded"
          >
            + Post Property
          </Link>
        </nav>
      </div>
    </header>
  );
};

export default Header;
