import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

const SettingsPage = () => {
  const [activeTab, setActiveTab] = useState("general");

  return (
    <div className="min-h-screen bg-[#08101f] text-white px-6 py-10 flex justify-center">
      <div className="w-full max-w-5xl bg-[#0f1a2f] rounded-xl shadow-xl p-0">

        {/* ---------- TOP TAB BAR ---------- */}
        <div className="flex border-b border-gray-700">
          {["general", "chat", "advanced"].map((tab) => (
            <button
              key={tab}
              className={`flex-1 text-center py-4 font-medium text-lg 
                ${activeTab === tab ? "bg-[#1e2a44] text-purple-400" : "text-gray-300"}
                hover:bg-[#162138] transition`}
              onClick={() => setActiveTab(tab)}
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1)}
            </button>
          ))}
        </div>

        {/* ---------- CONTENT BOX ---------- */}
        <div className="p-8 space-y-8">

          {/* ---------- GENERAL TAB ---------- */}
          {activeTab === "general" && (
            <div className="space-y-6">
              <SettingRow
                label="Currency:"
                input={
                  <select className="input-select">
                    <option>USD</option>
                    <option>EUR</option>
                    <option>INR</option>
                  </select>
                }
              />

              <SettingRow
                label="Language:"
                input={
                  <select className="input-select">
                    <option>English</option>
                    <option>Spanish</option>
                    <option>Hindi</option>
                  </select>
                }
              />

              <SettingRow
                label="Phone Number:"
                input={
                  <select className="input-select">
                    <option>Visible to Everyone</option>
                    <option>Visible to Only My Customers</option>
                  </select>
                }
              />

              <SettingRow
                label="Profile:"
                input={
                  <select className="input-select">
                    <option>Visible to Everyone</option>
                    <option>Visible to Only My Customers</option>
                  </select>
                }
              />
            </div>
          )}

          {/* ---------- CHAT TAB ---------- */}
          {activeTab === "chat" && (
            <div className="space-y-6">
              <SettingRow
                label="Font Size:"
                input={
                  <select className="input-select">
                    <option>Small</option>
                    <option>Medium</option>
                    <option>Large</option>
                  </select>
                }
              />

              <ToggleRow label="Chat Enabled" />
              <ToggleRow label="Email Notification" />
              <ToggleRow label="Backup Chat" />

              <SettingRow
                label="Delete Chat:"
                input={
                  <select className="input-select">
                    <option>Delete Read Chat</option>
                    <option>Delete All</option>
                  </select>
                }
              />
            </div>
          )}

          {/* ---------- ADVANCED TAB ---------- */}
          {activeTab === "advanced" && (
            <div className="space-y-6">

              <ToggleRow label="Auto Advance" />

              <RadioRow
                label="Color Theme:"
                options={["Light", "Dark"]}
              />

              <RadioRow
                label="Posted ADs:"
                options={["Directly", "By Admin Confirmation"]}
              />

              <RadioRow
                label="Comments Visible:"
                options={["Directly", "By Admin Confirmation"]}
              />

              <ToggleRow label="System Notification" />
              <ToggleRow label="Mail Notification" />
              <ToggleRow label="Auto Watch Comments" />
            </div>
          )}

          {/* ---------- BUTTONS ---------- */}
          <div className="flex gap-4 pt-4">
            <button className="px-6 py-2 bg-purple-600 hover:bg-purple-700 rounded font-medium">
              Save Changes
            </button>
            <button className="px-6 py-2 bg-pink-600 hover:bg-pink-700 rounded font-medium">
              Cancel
            </button>
          </div>

        </div>
      </div>
    </div>
  );
};

/* ---------- REUSABLE COMPONENTS ---------- */

const SettingRow = ({ label, input }) => (
  <div className="flex justify-between items-center">
    <span className="text-lg w-56">{label}</span>
    <div className="flex-1">{input}</div>
  </div>
);

const ToggleRow = ({ label }) => (
  <div className="flex justify-between items-center">
    <span className="text-lg w-56">{label}</span>
    <label className="relative inline-flex cursor-pointer items-center">
      <input type="checkbox" className="peer sr-only" />
      <div className="h-6 w-11 rounded-full bg-gray-600 peer-checked:bg-purple-600 transition"></div>
      <div className="absolute left-[3px] top-[3px] h-5 w-5 rounded-full bg-white peer-checked:translate-x-5 transition"></div>
    </label>
  </div>
);

const RadioRow = ({ label, options }) => (
  <div className="flex items-center">
    <span className="text-lg w-56">{label}</span>
    <div className="flex gap-5">
      {options.map((opt) => (
        <label key={opt} className="flex items-center gap-2">
          <input type="radio" name={label} />
          <span>{opt}</span>
        </label>
      ))}
    </div>
  </div>
);

/* Tailwind extra styles */
const styles = `
.input-select {
  @apply bg-[#1b253b] border border-gray-600 text-white p-2 rounded w-64 focus:outline-none;
}
`;
export default SettingsPage;
