import React from "react";
import {
  FaBuilding,
  FaHotel,
  FaCity,
  FaTree,
  FaHome,
  FaBriefcase,
} from "react-icons/fa";

const CategorySlider = () => {
  const categories = [
    { name: "Kids House", icon: <FaHome />, count: 12 },
    { name: "Budget House", icon: <FaBuilding />, count: 18 },
    { name: "Luxury House", icon: <FaHotel />, count: 8 },
    { name: "Stylish House", icon: <FaCity />, count: 14 },
    { name: "Apartments", icon: <FaHome />, count: 22 },
    { name: "Farmhouse", icon: <FaTree />, count: 9 },
    { name: "Deluxe House", icon: <FaHome />, count: 7 },
    { name: "Nature House", icon: <FaTree />, count: 11 },
    { name: "2 BHK", icon: <FaHome />, count: 16 },
    { name: "3 BHK", icon: <FaHome />, count: 10 },
    { name: "Office Rooms", icon: <FaBriefcase />, count: 5 },
  ];

  const loopCategories = [...categories, ...categories];

  return (
    <div
      className="w-full overflow-hidden py-12"
      style={{
        backgroundImage:
          'url("https://images.unsplash.com/photo-1507034589631-9433cc6bc453?auto=format&fit=crop&w=1400&q=80")',
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div className="bg-[#07182E]/80 backdrop-blur-sm py-10">
        <h2 className="text-white text-3xl font-bold text-center mb-6">
          Popular Categories
        </h2>

        <div className="relative w-full overflow-hidden">
          <div className="flex gap-6 animate-slide">
            {loopCategories.map((item, index) => (
              <div
                key={index}
                className="min-w-[210px] bg-white/10 backdrop-blur-md border border-white/20 
                p-4 rounded-xl flex items-center gap-4 shadow-lg hover:scale-105 
                transition-all cursor-pointer text-white"
              >
                <div className="text-blue-300 text-3xl">{item.icon}</div>

                <div>
                  <p className="font-semibold">{item.name}</p>
                  <p className="text-sm opacity-80">{item.count} available</p>
                </div>
              </div>
            ))}
          </div>
        </div>

        <style>{`
          .animate-slide {
            display: flex;
            width: max-content;
            animation: scroll 22s linear infinite;
          }
          @keyframes scroll {
            0% { transform: translateX(0); }
            100% { transform: translateX(-50%); }
          }
        `}</style>
      </div>
    </div>
  );
};

export default CategorySlider;
