import React from "react";
import {
  FaHome,
  FaBuilding,
  FaHotel,
  FaCity,
  FaTree,
  FaBriefcase,
} from "react-icons/fa";

const CategorySlider = () => {
  const categories = [
    {
      name: "Kids House",
      icon: <FaHome />,
      count: 12,
      img: "https://images.unsplash.com/photo-1586105251261-72a756497a11?auto=format&fit=crop&w=600&q=60",
    },
    {
      name: "Budget House",
      icon: <FaBuilding />,
      count: 18,
      img: "https://images.unsplash.com/photo-1570129477492-45c003edd2be?auto=format&fit=crop&w=600&q=60",
    },
    {
      name: "Luxury House",
      icon: <FaHotel />,
      count: 8,
      img: "https://images.unsplash.com/photo-1501183638710-841dd1904471?auto=format&fit=crop&w=600&q=60",
    },
    {
      name: "Stylish House",
      icon: <FaCity />,
      count: 14,
      img: "https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?auto=format&fit=crop&w=600&q=60",
    },
    {
      name: "Apartments",
      icon: <FaHome />,
      count: 22,
      img: "https://images.unsplash.com/photo-1486304873000-235643847519?auto=format&fit=crop&w=600&q=60",
    },
    {
      name: "Farmhouse",
      icon: <FaTree />,
      count: 9,
      img: "https://images.unsplash.com/photo-1505691938895-1758d7feb511?auto=format&fit=crop&w=600&q=60",
    },
    {
      name: "Deluxe House",
      icon: <FaHome />,
      count: 7,
      img: "https://images.unsplash.com/photo-1507089947368-19c1da9775ae?auto=format&fit=crop&w=600&q=60",
    },
    {
      name: "Nature House",
      icon: <FaTree />,
      count: 11,
      img: "https://images.unsplash.com/photo-1505692769274-513d5a9c40b7?auto=format&fit=crop&w=600&q=60",
    },
    {
      name: "2 BHK",
      icon: <FaHome />,
      count: 16,
      img: "https://images.unsplash.com/photo-1580587771525-78b9dba3b914?auto=format&fit=crop&w=600&q=60",
    },
    {
      name: "3 BHK",
      icon: <FaHome />,
      count: 10,
      img: "https://images.unsplash.com/photo-1556912998-6a321d550f86?auto=format&fit=crop&w=600&q=60",
    },
    {
      name: "Office Rooms",
      icon: <FaBriefcase />,
      count: 5,
      img: "https://images.unsplash.com/photo-1570129477492-45c003edd2be?auto=format&fit=crop&w=600&q=60",
    },
  ];

  const loopCategories = [...categories, ...categories];

  return (
    <div className="w-full overflow-hidden bg-[#0A1A2F] py-10">
      <h2 className="text-white text-3xl font-bold text-center mb-6">
        Popular Categories
      </h2>

      <div className="relative w-full">
        <div className="flex gap-6 animate-slide">
          {loopCategories.map((item, i) => (
            <div
              key={i}
              className="min-w-[230px] h-[120px] rounded-xl shadow-lg relative 
              overflow-hidden cursor-pointer hover:scale-105 transition-all"
            >
              {/* background image */}
              <div
                className="absolute inset-0 bg-cover bg-center opacity-60"
                style={{ backgroundImage: `url(${item.img})` }}
              ></div>

              {/* dark overlay */}
              <div className="absolute inset-0 bg-black/50"></div>

              {/* content */}
              <div className="relative h-full flex items-center gap-3 text-white px-4">
                <div className="text-blue-300 text-3xl">{item.icon}</div>
                <div>
                  <p className="font-semibold text-sm">{item.name}</p>
                  <p className="text-xs opacity-90">{item.count} available</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* animation css */}
      <style>{`
        .animate-slide {
          display: flex;
          width: max-content;
          animation: scroll 1s linear infinite;
        }
        @keyframes scroll {
          0% { transform: translateX(0); }
          100% { transform: translateX(-50%); }
        }
      `}</style>
    </div>
  );
};

export default CategorySlider;
