import React, { useRef, useEffect } from "react";
import { FaChevronLeft, FaChevronRight, FaShareAlt } from "react-icons/fa";
import { FaBed, FaCar, FaBath } from "react-icons/fa";

const featuredData = [
  {
    id: 1,
    title: "Premium 3 BHK Apartment",
    price: "$220,000",
    location: "Delhi, India",
    date: "12 Jan 2025",
    tag: "For Sale",
    beds: 3,
    baths: 2,
    parking: 1,
    image: "https://i.ibb.co/xDdK9R5/house-4.jpg",
    url: "#",
  },
  {
    id: 2,
    title: "Luxury Villa With Garden",
    price: "$350,000",
    location: "Mumbai, India",
    date: "03 Feb 2025",
    tag: "Sold",
    beds: 4,
    baths: 3,
    parking: 2,
    image: "https://i.ibb.co/sK44HnZ/house.jpg",
    url: "#",
  },
  {
    id: 3,
    title: "Modern Office Space",
    price: "$1500/month",
    location: "Pune, India",
    date: "22 Mar 2025",
    tag: "For Rent",
    beds: 0,
    baths: 2,
    parking: 1,
    image: "https://i.ibb.co/nRtK6D5/house-2.jpg",
    url: "#",
  },
  {
    id: 4,
    title: "2 BHK Family House",
    price: "$140,000",
    location: "Bangalore, India",
    date: "08 Apr 2025",
    tag: "New",
    beds: 2,
    baths: 2,
    parking: 1,
    image: "https://i.ibb.co/2q4JH6X/house-3.jpg",
    url: "#",
  },
 {
    id: 1,
    title: "Premium 3 BHK Apartment",
    price: "$220,000",
    location: "Delhi, India",
    date: "12 Jan 2025",
    tag: "For Sale",
    beds: 3,
    baths: 2,
    parking: 1,
    image: "https://i.ibb.co/xDdK9R5/house-4.jpg",
    url: "#",
  },
  {
    id: 2,
    title: "Luxury Villa With Garden",
    price: "$350,000",
    location: "Mumbai, India",
    date: "03 Feb 2025",
    tag: "Sold",
    beds: 4,
    baths: 3,
    parking: 2,
    image: "https://i.ibb.co/sK44HnZ/house.jpg",
    url: "#",
  },
  {
    id: 3,
    title: "Modern Office Space",
    price: "$1500/month",
    location: "Pune, India",
    date: "22 Mar 2025",
    tag: "For Rent",
    beds: 0,
    baths: 2,
    parking: 1,
    image: "https://i.ibb.co/nRtK6D5/house-2.jpg",
    url: "#",
  },
  {
    id: 4,
    title: "2 BHK Family House",
    price: "$140,000",
    location: "Bangalore, India",
    date: "08 Apr 2025",
    tag: "New",
    beds: 2,
    baths: 2,
    parking: 1,
    image: "https://i.ibb.co/2q4JH6X/house-3.jpg",
    url: "#",
  },
];

const FeaturedProperties = () => {
  const sliderRef = useRef(null);

  // Auto Slide
  useEffect(() => {
    const autoSlide = setInterval(() => {
      sliderRef.current.scrollBy({ left: 260, behavior: "smooth" });
    }, 2500);

    return () => clearInterval(autoSlide);
  }, []);

  const scrollLeft = () => {
    sliderRef.current.scrollBy({ left: -260, behavior: "smooth" });
  };

  const scrollRight = () => {
    sliderRef.current.scrollBy({ left: 260, behavior: "smooth" });
  };

  const shareProperty = (url) => {
    if (navigator.share) navigator.share({ title: "Check Property", url });
    else navigator.clipboard.writeText(url);
  };

  return (
    <section className="max-w-[1300px] mx-auto px-4 py-10">
      <h2 className="text-3xl font-bold text-center text-white mb-6">
        Featured <span className="text-blue-500">Properties</span>
      </h2>

      <div className="relative">

        {/* LEFT BUTTON */}
        <button
          onClick={scrollLeft}
          className="absolute left-1 top-1/2 -translate-y-1/2 z-20
          bg-white/30 backdrop-blur-xl border border-white/20 shadow-lg
          p-2 rounded-full hover:scale-110 duration-300"
        >
          <FaChevronLeft className="text-white" size={18} />
        </button>

        {/* SLIDER */}
        <div
          ref={sliderRef}
          className="flex gap-4 overflow-x-auto py-4 scroll-smooth scrollbar-none"
        >
          {featuredData.map((p) => (
            <div
              key={p.id}
              className="bg-white/90 backdrop-blur-lg shadow-lg rounded-xl overflow-hidden 
              min-w-[190px] sm:min-w-[220px] md:min-w-[260px] hover:shadow-2xl duration-300"
            >
              <div className="relative">
    
                {/* IMAGE */}
                <img src={p.image} className="w-full h-36 object-cover" alt="" />

                {/* TAG */}
                <span className="absolute top-2 left-2 bg-blue-600 text-white text-[11px] py-1 px-2 rounded-md shadow-md">
                  {p.tag}
                </span>
              </div>

              {/* CONTENT */}
              <div className="p-3">
                <h3 className="text-[14px] font-semibold text-gray-800">{p.title}</h3>

                <p className="text-blue-600 font-bold text-sm mt-1">{p.price}</p>
                <p className="text-gray-500 text-xs">{p.location}</p>

                {/* DETAILS (Beds, Bath, Parking) */}
                <div className="flex justify-between mt-3 text-[12px] font-medium text-gray-700">
                  <div className="flex items-center gap-1">
                    <FaBed className="text-blue-600" />
                    {p.beds} Beds
                  </div>
                  <div className="flex items-center gap-1">
                    <FaBath className="text-blue-600" />
                    {p.baths} Bath
                  </div>
                  <div className="flex items-center gap-1">
                    <FaCar className="text-blue-600" />
                    {p.parking} Car
                  </div>
                </div>

                {/* DATE + SHARE (BLUE GLASS STRIP) */}
                <div
                  className="mt-3 bg-blue-500/30 backdrop-blur-lg 
                  border border-blue-300/40 py-1 px-3 rounded-xl shadow-sm
                  flex justify-between items-center"
                >
                  <span className="text-[11px] font-semibold text-blue-900">
                    {p.date}
                  </span>

                  <button onClick={() => shareProperty(p.url)}>
                    <FaShareAlt className="text-blue-900 hover:scale-110 duration-300" size={14} />
                  </button>
                </div>

              </div>
            </div>
          ))}
        </div>

        {/* RIGHT BUTTON */}
        <button
          onClick={scrollRight}
          className="absolute right-1 top-1/2 -translate-y-1/2 z-20
          bg-white/30 backdrop-blur-xl border border-white/20 shadow-lg
          p-2 rounded-full hover:scale-110 duration-300"
        >
          <FaChevronRight className="text-white" size={18} />
        </button>

      </div>
    </section>
  );
};

export default FeaturedProperties;
