import React, { useState } from "react";
import Sidebar from "../components/Sidebar"; // Import Sidebar Component
import { Link } from "react-router-dom"; // Import Link for navigation
const MyProfile = () => {
  const [profile, setProfile] = useState({
    firstName: "John",
    lastName: "Doe",
    phone: "1234567890",
    email: "john.doe@example.com",
    address: "",
    city: "",
    postalCode: "",
    country: "India",
    about: "",
  });

  const [sidebarOpen, setSidebarOpen] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  return (
    <div className="min-h-screen bg-[#0a1124] pt-">
      
    {/* HEADER */}
<div
  className="w-full h-56 relative rounded-xl overflow-hidden shadow mb-6"
>
  {/* Background Image */}
  <img
    src="https://images.unsplash.com/photo-1529626455594-4ff0802cfb7e?auto=format"
    alt="Dashboard Header"
    className="w-full h-full object-cover"
  />

  {/* Dark Gradient Overlay */}
  <div className="absolute inset-0 bg-gradient-to-r from-[#0B64BD]/70 to-[#3FA1E6]/70 flex items-center justify-center">
    <h1 className="text-white text-3xl font-bold">My Dashboard</h1>
  </div>
</div>


      {/* Mobile Sidebar Toggle */}
      <button
        className="md:hidden mb-4 px-4 py-2 bg-gradient-to-r 
        from-[#0B64BD] to-[#3FA1E6] text-white rounded-lg"
        onClick={() => setSidebarOpen(!sidebarOpen)}
      >
        {sidebarOpen ? "Close Menu" : "Open Menu"}
      </button>

      {/* MAIN LAYOUT */}
      <div className="flex flex-col md:flex-row gap-6 max-w-6xl mx-auto">

     


        {/* ---------------- RIGHT FORM SECTION ---------------- */}
        <div className="flex-1 bg-[#111827] p-8 rounded-xl shadow-lg text-white">

          <h2 className="text-xl font-semibold mb-6">Edit Profile</h2>

          {/* FORM INPUTS */}
          <div className="grid md:grid-cols-2 gap-6 mb-4">
            <Input label="First Name" placeholder="John" />
            <Input label="Last Name" placeholder="Doe" />
          </div>

          <div className="grid md:grid-cols-2 gap-6 mb-4">
            <Input label="Email" placeholder="john@example.com" />
            <Input label="Phone Number" placeholder="1234567890" />
          </div>

          <Input label="Address" placeholder="Home Address" />

          <div className="grid md:grid-cols-3 gap-6 my-4">
            <Input label="City" placeholder="City" />
            <Input label="Postal Code" placeholder="ZIP Code" />
            <Select label="Country" options={["India", "USA", "UK"]} />
          </div>

          <div className="grid md:grid-cols-2 gap-6 mb-4">
            <Input label="Facebook" placeholder="https://facebook.com/" />
            <Input label="Google" placeholder="https://google.com/" />
          </div>

          <div className="grid md:grid-cols-2 gap-6 mb-4">
            <Input label="Twitter" placeholder="https://twitter.com/" />
            <Input label="Pinterest" placeholder="https://pinterest.com/" />
          </div>

          <div className="mb-4">
            <label className="block mb-1 font-medium">About Me</label>
            <textarea
              rows="3"
              className="w-full bg-gray-900 border border-gray-600 rounded-md p-3"
              placeholder="Enter about your description"
            ></textarea>
          </div>

          <button className="px-6 py-3 text-white rounded-lg 
            bg-gradient-to-r from-[#0B64BD] to-[#3FA1E6]">
            Save Changes
          </button>
        </div>
      </div>
    </div>
  );
};

/* REUSABLE INPUT */
const Input = ({ label, placeholder }) => (
  <div>
    <label className="block mb-1 font-medium">{label}</label>
    <input
      className="w-full bg-gray-900 border border-gray-600 rounded-md p-3"
      placeholder={placeholder}
    />
  </div>
);

/* REUSABLE SELECT */
const Select = ({ label, options }) => (
  <div>
    <label className="block mb-1 font-medium">{label}</label>
    <select className="w-full bg-gray-900 border border-gray-600 rounded-md p-3">
      {options.map((opt) => (
        <option key={opt}>{opt}</option>
      ))}
    </select>
  </div>
);

export default MyProfile;
